import { Provider, Contract, EventLog } from 'ethers';
import { Diamond } from 'diamonds';
import winston from 'winston';

/**
 * Configuration options for DiamondMonitor
 */
export interface DiamondMonitorConfig {
  /** Polling interval in milliseconds for health checks (default: 30000) */
  pollingInterval?: number;
  /** Enable event logging (default: true) */
  enableEventLogging?: boolean;
  /** Enable automated health checks (default: true) */
  enableHealthChecks?: boolean;
  /** Custom logger instance */
  logger?: winston.Logger;
  /** Block number to start monitoring from */
  fromBlock?: number | 'latest';
}

/**
 * Internal configuration with all required properties
 */
interface InternalConfig {
  pollingInterval: number;
  enableEventLogging: boolean;
  enableHealthChecks: boolean;
  logger: winston.Logger;
  fromBlock: number | 'latest';
}

/**
 * Diamond information structure
 */
export interface DiamondInfo {
  /** Diamond contract address */
  address: string;
  /** Array of facet information */
  facets: FacetInfo[];
  /** Total number of function selectors */
  totalSelectors: number;
  /** Diamond ABI */
  abi?: any[];
}

/**
 * Facet information structure
 */
export interface FacetInfo {
  /** Facet contract address */
  address: string;
  /** Function selectors handled by this facet */
  selectors: string[];
  /** Facet name (if available) */
  name?: string;
}

/**
 * Health check result structure
 */
export interface HealthCheckResult {
  /** Overall health status */
  isHealthy: boolean;
  /** Individual health checks */
  checks: HealthCheck[];
  /** Timestamp of the health check */
  timestamp: Date;
  /** Total time taken for all checks */
  totalTime: number;
}

/**
 * Individual health check structure
 */
export interface HealthCheck {
  /** Name of the health check */
  name: string;
  /** Check status */
  status: 'passed' | 'failed' | 'warning';
  /** Check message */
  message: string;
  /** Time taken for this check */
  duration: number;
  /** Additional details */
  details?: any;
}

/**
 * Event listener function type
 */
export type EventListener = (event: EventLog) => void;

/**
 * Monitoring errors
 */
export class MonitoringError extends Error {
  constructor(message: string, public readonly cause?: Error) {
    super(message);
    this.name = 'MonitoringError';
  }
}

/**
 * Main DiamondMonitor class for monitoring ERC-2535 Diamond Proxy contracts
 * 
 * This class provides comprehensive monitoring capabilities including:
 * - Real-time event monitoring
 * - Health checks and diagnostics
 * - Facet management and analysis
 * - Integration with the diamonds module
 */
export class DiamondMonitor {
  private readonly diamond: Diamond;
  private readonly provider: Provider;
  private readonly config: InternalConfig;
  private readonly logger: winston.Logger;

  private isActive = false;
  private contract?: Contract;
  private eventListeners: EventListener[] = [];
  private healthCheckInterval?: NodeJS.Timeout;

  /**
   * Creates a new DiamondMonitor instance
   * 
   * @param diamond - Diamond instance from the diamonds module
   * @param provider - Ethereum provider (ethers.js)
   * @param config - Optional configuration
   */
  constructor(
    diamond: Diamond,
    provider: Provider,
    config: DiamondMonitorConfig = {}
  ) {
    if (!diamond) {
      throw new MonitoringError('Diamond instance is required');
    }
    if (!provider) {
      throw new MonitoringError('Provider is required');
    }

    this.diamond = diamond;
    this.provider = provider;

    // Merge config with defaults
    this.config = {
      pollingInterval: config.pollingInterval ?? 30000,
      enableEventLogging: config.enableEventLogging ?? true,
      enableHealthChecks: config.enableHealthChecks ?? true,
      logger: config.logger ?? this.createDefaultLogger(),
      fromBlock: config.fromBlock ?? 'latest'
    };

    this.logger = this.config.logger;
    this.logger.info('DiamondMonitor initialized', {
      diamondAddress: this.getDiamondAddress(),
      config: this.config
    });
  }

  /**
   * Create default winston logger
   */
  private createDefaultLogger(): winston.Logger {
    return winston.createLogger({
      level: 'info',
      format: winston.format.combine(
        winston.format.timestamp(),
        winston.format.errors({ stack: true }),
        winston.format.json()
      ),
      transports: [
        new winston.transports.Console({
          format: winston.format.simple()
        })
      ]
    });
  }

  /**
   * Get diamond address from Diamond instance
   */
  private getDiamondAddress(): string {
    try {
      const deployedData = this.diamond.getDeployedDiamondData();
      return deployedData?.DiamondAddress || '';
    } catch (error) {
      throw new MonitoringError('Failed to get diamond address from Diamond instance', error as Error);
    }
  }

  /**
   * Initialize contract instance for monitoring
   */
  private async initializeContract(): Promise<void> {
    try {
      const diamondAddress = this.getDiamondAddress();
      // For now, we'll use a basic Diamond ABI - this can be enhanced later
      const basicAbi = [
        'event DiamondCut(tuple(address facetAddress, uint8 action, bytes4[] functionSelectors)[] _diamondCut, address _init, bytes _calldata)',
        'function facets() external view returns (tuple(address facetAddress, bytes4[] functionSelectors)[])',
        'function facetFunctionSelectors(address _facet) external view returns (bytes4[])',
        'function facetAddresses() external view returns (address[])',
        'function facetAddress(bytes4 _functionSelector) external view returns (address)'
      ];

      this.contract = new Contract(diamondAddress, basicAbi, this.provider);
      this.logger.debug('Contract initialized', { address: diamondAddress });
    } catch (error) {
      throw new MonitoringError('Failed to initialize contract', error as Error);
    }
  }

  /**
   * Get comprehensive diamond information
   * 
   * @returns Promise resolving to diamond information
   */
  public async getDiamondInfo(): Promise<DiamondInfo> {
    try {
      const deployedData = this.diamond.getDeployedDiamondData();

      // Transform deployed facets data to our format
      const facets: FacetInfo[] = [];
      if (deployedData?.DeployedFacets) {
        Object.entries(deployedData.DeployedFacets).forEach(([name, facetData]) => {
          if (facetData?.address && facetData?.funcSelectors) {
            facets.push({
              address: facetData.address,
              selectors: facetData.funcSelectors,
              name: name
            });
          }
        });
      }

      const totalSelectors = facets.reduce((total, facet) => total + facet.selectors.length, 0);

      return {
        address: deployedData?.DiamondAddress || '',
        facets,
        totalSelectors,
        abi: [] // ABI will be populated separately if needed
      };
    } catch (error) {
      throw new MonitoringError('Failed to get diamond information', error as Error);
    }
  }

  /**
   * Start monitoring the diamond contract
   * 
   * @returns Promise that resolves when monitoring is started
   */
  public async startMonitoring(): Promise<void> {
    if (this.isActive) {
      this.logger.warn('Monitoring is already active');
      return;
    }

    try {
      await this.initializeContract();

      if (this.config.enableHealthChecks) {
        this.startHealthChecks();
      }

      this.isActive = true;
      this.logger.info('Diamond monitoring started');
    } catch (error) {
      throw new MonitoringError('Failed to start monitoring', error as Error);
    }
  }

  /**
   * Stop monitoring the diamond contract
   */
  public stopMonitoring(): void {
    if (!this.isActive) {
      return;
    }

    this.stopHealthChecks();
    this.eventListeners = [];
    this.isActive = false;

    this.logger.info('Diamond monitoring stopped');
  }

  /**
   * Check if monitoring is currently active
   * 
   * @returns True if monitoring is active
   */
  public isMonitoring(): boolean {
    return this.isActive;
  }

  /**
   * Perform comprehensive health checks on the diamond contract
   * 
   * @returns Promise resolving to health check results
   */
  public async getHealthStatus(): Promise<HealthCheckResult> {
    const startTime = Date.now();
    const checks: HealthCheck[] = [];

    try {
      // Connectivity check
      const connectivityCheck = await this.performConnectivityCheck();
      checks.push(connectivityCheck);

      // Contract existence check
      const contractCheck = await this.performContractCheck();
      checks.push(contractCheck);

      // Facet integrity check
      const facetCheck = await this.performFacetIntegrityCheck();
      checks.push(facetCheck);

    } catch (error) {
      checks.push({
        name: 'health_check_error',
        status: 'failed',
        message: `Health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        duration: 0,
        details: { error }
      });
    }

    const totalTime = Date.now() - startTime;
    const isHealthy = checks.every(check => check.status === 'passed');

    return {
      isHealthy,
      checks,
      timestamp: new Date(),
      totalTime
    };
  }

  /**
   * Set up event tracking for diamond contract events
   * 
   * @param listener - Optional custom event listener
   * @returns Promise that resolves when event tracking is set up
   */
  public async trackEvents(listener?: EventListener): Promise<void> {
    if (!this.contract) {
      await this.initializeContract();
    }

    if (listener) {
      this.eventListeners.push(listener);
    }

    if (this.config.enableEventLogging) {
      this.setupEventLogging();
    }

    this.logger.info('Event tracking initialized');
  }

  /**
   * Start automated health checks
   */
  private startHealthChecks(): void {
    this.healthCheckInterval = setInterval(async () => {
      try {
        const health = await this.getHealthStatus();
        if (!health.isHealthy) {
          this.logger.warn('Health check failed', { checks: health.checks });
        }
      } catch (error) {
        this.logger.error('Health check error', { error });
      }
    }, this.config.pollingInterval);
  }

  /**
   * Stop automated health checks
   */
  private stopHealthChecks(): void {
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = undefined;
    }
  }

  /**
   * Perform connectivity check
   */
  private async performConnectivityCheck(): Promise<HealthCheck> {
    const startTime = Date.now();

    try {
      await this.provider.getNetwork();

      return {
        name: 'connectivity',
        status: 'passed',
        message: 'Provider connectivity is healthy',
        duration: Date.now() - startTime
      };
    } catch (error) {
      return {
        name: 'connectivity',
        status: 'failed',
        message: `Provider connectivity failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        duration: Date.now() - startTime,
        details: { error }
      };
    }
  }

  /**
   * Perform contract existence check
   */
  private async performContractCheck(): Promise<HealthCheck> {
    const startTime = Date.now();

    try {
      const diamondAddress = this.getDiamondAddress();
      const code = await this.provider.getCode(diamondAddress);

      if (code === '0x') {
        return {
          name: 'contract_existence',
          status: 'failed',
          message: 'Diamond contract not found at address',
          duration: Date.now() - startTime,
          details: { address: diamondAddress }
        };
      }

      return {
        name: 'contract_existence',
        status: 'passed',
        message: 'Diamond contract exists and has code',
        duration: Date.now() - startTime
      };
    } catch (error) {
      return {
        name: 'contract_existence',
        status: 'failed',
        message: `Contract check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        duration: Date.now() - startTime,
        details: { error }
      };
    }
  }

  /**
   * Perform facet integrity check
   */
  private async performFacetIntegrityCheck(): Promise<HealthCheck> {
    const startTime = Date.now();

    try {
      const info = await this.getDiamondInfo();

      if (info.facets.length === 0) {
        return {
          name: 'facet_integrity',
          status: 'warning',
          message: 'No facets found in diamond',
          duration: Date.now() - startTime
        };
      }

      // Check for empty selectors
      const emptyFacets = info.facets.filter(facet => facet.selectors.length === 0);
      if (emptyFacets.length > 0) {
        return {
          name: 'facet_integrity',
          status: 'warning',
          message: `Found ${emptyFacets.length} facets with no selectors`,
          duration: Date.now() - startTime,
          details: { emptyFacets }
        };
      }

      return {
        name: 'facet_integrity',
        status: 'passed',
        message: `All ${info.facets.length} facets have selectors`,
        duration: Date.now() - startTime
      };
    } catch (error) {
      return {
        name: 'facet_integrity',
        status: 'failed',
        message: `Facet integrity check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        duration: Date.now() - startTime,
        details: { error }
      };
    }
  }

  /**
   * Setup event logging for contract events
   */
  private setupEventLogging(): void {
    if (!this.contract) {
      return;
    }

    // Listen for DiamondCut events
    this.contract.on('DiamondCut', (...args) => {
      const event = args[args.length - 1] as EventLog;
      this.logger.info('DiamondCut event detected', { event });

      // Notify all listeners
      this.eventListeners.forEach(listener => {
        try {
          listener(event);
        } catch (error) {
          this.logger.error('Event listener error', { error });
        }
      });
    });

    this.logger.debug('Event logging setup completed');
  }
}

import { Provider } from 'ethers';
import { Diamond } from 'diamonds';

/**
 * Facet information structure
 */
export interface FacetInfo {
  /** Facet name */
  name: string;
  /** Facet contract address */
  address: string;
  /** Function selectors handled by this facet */
  selectors: string[];
}

/**
 * Selector conflict information
 */
export interface SelectorConflict {
  /** The conflicting selector */
  selector: string;
  /** Name of the existing facet that has this selector */
  existingFacet: string;
  /** Address of the existing facet */
  existingFacetAddress: string;
}

/**
 * Selector validation result
 */
export interface SelectorValidationResult {
  /** Whether the selectors are valid (no conflicts) */
  isValid: boolean;
  /** Array of conflicts found */
  conflicts: SelectorConflict[];
  /** Additional validation messages */
  messages: string[];
}

/**
 * Facet analysis result
 */
export interface FacetAnalysisResult {
  /** Total number of facets */
  totalFacets: number;
  /** Total number of unique selectors */
  totalSelectors: number;
  /** Average selectors per facet */
  averageSelectorsPerFacet: number;
  /** Conflicts found during analysis */
  conflicts: SelectorConflict[];
  /** Additional analysis details */
  details: {
    /** Largest facet by selector count */
    largestFacet?: {
      name: string;
      selectorCount: number;
    };
    /** Smallest facet by selector count */
    smallestFacet?: {
      name: string;
      selectorCount: number;
    };
    /** Facets with no selectors */
    emptyFacets: string[];
  };
}

/**
 * Diamond cut action types
 */
export enum FacetCutAction {
  Add = 0,
  Replace = 1,
  Remove = 2
}

/**
 * Diamond cut structure
 */
export interface DiamondCut {
  /** Target facet address */
  facetAddress: string;
  /** Action to perform */
  action: FacetCutAction;
  /** Function selectors to operate on */
  functionSelectors: string[];
}

/**
 * Diamond cut validation result
 */
export interface DiamondCutValidationResult {
  /** Whether the cut is valid */
  isValid: boolean;
  /** Validation errors */
  errors: string[];
  /** Validation warnings */
  warnings: string[];
  /** Summary of changes */
  summary: {
    /** Number of selectors being added */
    addedSelectors: number;
    /** Number of selectors being replaced */
    replacedSelectors: number;
    /** Number of selectors being removed */
    removedSelectors: number;
  };
}

/**
 * FacetManager errors
 */
export class FacetManagerError extends Error {
  constructor(message: string, public readonly cause?: Error) {
    super(message);
    this.name = 'FacetManagerError';
  }
}

/**
 * FacetManager class for managing diamond facets
 * 
 * This class provides comprehensive facet management capabilities including:
 * - Listing and analyzing facets
 * - Validating function selectors
 * - Creating diamond cuts
 * - Validating diamond cuts before execution
 */
export class FacetManager {
  private readonly diamond: Diamond;
  private readonly provider: Provider;

  /**
   * Creates a new FacetManager instance
   * 
   * @param diamond - Diamond instance from the diamonds module
   * @param provider - Ethereum provider (ethers.js)
   */
  constructor(diamond: Diamond, provider: Provider) {
    if (!diamond) {
      throw new FacetManagerError('Diamond instance is required');
    }
    if (!provider) {
      throw new FacetManagerError('Provider is required');
    }

    this.diamond = diamond;
    this.provider = provider;
  }

  /**
   * List all facets in the diamond
   * 
   * @returns Promise resolving to array of facet information
   */
  public async listFacets(): Promise<FacetInfo[]> {
    try {
      const deployedData = this.diamond.getDeployedDiamondData();
      const facets: FacetInfo[] = [];

      if (deployedData?.DeployedFacets) {
        Object.entries(deployedData.DeployedFacets).forEach(([name, facetData]) => {
          if (facetData?.address && facetData?.funcSelectors) {
            facets.push({
              name,
              address: facetData.address,
              selectors: facetData.funcSelectors
            });
          }
        });
      }

      return facets;
    } catch (error) {
      throw new FacetManagerError('Failed to list facets', error as Error);
    }
  }

  /**
   * Get function selectors for a specific facet
   * 
   * @param facetIdentifier - Facet name or address
   * @returns Promise resolving to array of function selectors
   */
  public async getSelectorsForFacet(facetIdentifier: string): Promise<string[]> {
    try {
      const facets = await this.listFacets();

      // Search by name first, then by address
      const facet = facets.find(f => 
        f.name === facetIdentifier || 
        f.address.toLowerCase() === facetIdentifier.toLowerCase()
      );

      return facet ? facet.selectors : [];
    } catch (error) {
      throw new FacetManagerError(`Failed to get selectors for facet ${facetIdentifier}`, error as Error);
    }
  }

  /**
   * Validate function selectors for conflicts
   * 
   * @param newSelectors - Optional array of new selectors to validate against existing ones
   * @returns Promise resolving to validation result
   */
  public async validateSelectors(newSelectors?: string[]): Promise<SelectorValidationResult> {
    try {
      const facets = await this.listFacets();
      const conflicts: SelectorConflict[] = [];
      const messages: string[] = [];

      // Build map of existing selectors
      const existingSelectors = new Map<string, { facet: string; address: string }>();

      for (const facet of facets) {
        for (const selector of facet.selectors) {
          if (existingSelectors.has(selector)) {
            // Conflict within existing facets
            const existing = existingSelectors.get(selector)!;
            conflicts.push({
              selector,
              existingFacet: existing.facet,
              existingFacetAddress: existing.address
            });
          } else {
            existingSelectors.set(selector, {
              facet: facet.name,
              address: facet.address
            });
          }
        }
      }

      // Check new selectors against existing ones if provided
      if (newSelectors) {
        for (const selector of newSelectors) {
          if (existingSelectors.has(selector)) {
            const existing = existingSelectors.get(selector)!;
            conflicts.push({
              selector,
              existingFacet: existing.facet,
              existingFacetAddress: existing.address
            });
          }
        }
      }

      const isValid = conflicts.length === 0;

      if (!isValid) {
        messages.push(`Found ${conflicts.length} selector conflict(s)`);
      } else {
        messages.push('No selector conflicts detected');
      }

      return {
        isValid,
        conflicts,
        messages
      };
    } catch (error) {
      throw new FacetManagerError('Failed to validate selectors', error as Error);
    }
  }

  /**
   * Analyze facets for insights and potential issues
   * 
   * @param facets - Array of facets to analyze (defaults to current facets)
   * @returns Promise resolving to analysis result
   */
  public async analyzeFacets(facets?: FacetInfo[]): Promise<FacetAnalysisResult> {
    try {
      const facetsToAnalyze = facets || await this.listFacets();

      // Calculate basic statistics
      const totalFacets = facetsToAnalyze.length;
      const allSelectors = facetsToAnalyze.flatMap(f => f.selectors);
      const uniqueSelectors = new Set(allSelectors);
      const totalSelectors = uniqueSelectors.size;
      const averageSelectorsPerFacet = totalFacets > 0 ? Math.round((allSelectors.length / totalFacets) * 100) / 100 : 0;

      // Find conflicts
      const selectorMap = new Map<string, FacetInfo>();
      const conflicts: SelectorConflict[] = [];

      for (const facet of facetsToAnalyze) {
        for (const selector of facet.selectors) {
          if (selectorMap.has(selector)) {
            const existingFacet = selectorMap.get(selector)!;
            conflicts.push({
              selector,
              existingFacet: existingFacet.name,
              existingFacetAddress: existingFacet.address
            });
          } else {
            selectorMap.set(selector, facet);
          }
        }
      }

      // Find largest and smallest facets
      let largestFacet: { name: string; selectorCount: number } | undefined;
      let smallestFacet: { name: string; selectorCount: number } | undefined;
      const emptyFacets: string[] = [];

      for (const facet of facetsToAnalyze) {
        const selectorCount = facet.selectors.length;

        if (selectorCount === 0) {
          emptyFacets.push(facet.name);
        }

        if (!largestFacet || selectorCount > largestFacet.selectorCount) {
          largestFacet = { name: facet.name, selectorCount };
        }

        if (!smallestFacet || selectorCount < smallestFacet.selectorCount) {
          smallestFacet = { name: facet.name, selectorCount };
        }
      }

      return {
        totalFacets,
        totalSelectors,
        averageSelectorsPerFacet,
        conflicts,
        details: {
          largestFacet,
          smallestFacet,
          emptyFacets
        }
      };
    } catch (error) {
      throw new FacetManagerError('Failed to analyze facets', error as Error);
    }
  }

  /**
   * Create a diamond cut for adding a facet
   * 
   * @param facetAddress - Address of the facet to add
   * @param selectors - Function selectors to add
   * @returns Diamond cut structure
   */
  public createAddFacetCut(facetAddress: string, selectors: string[]): DiamondCut {
    return {
      facetAddress,
      action: FacetCutAction.Add,
      functionSelectors: [...selectors]
    };
  }

  /**
   * Create a diamond cut for replacing facet selectors
   * 
   * @param facetAddress - Address of the new facet
   * @param selectors - Function selectors to replace
   * @returns Diamond cut structure
   */
  public createReplaceFacetCut(facetAddress: string, selectors: string[]): DiamondCut {
    return {
      facetAddress,
      action: FacetCutAction.Replace,
      functionSelectors: [...selectors]
    };
  }

  /**
   * Create a diamond cut for removing selectors
   * 
   * @param selectors - Function selectors to remove
   * @returns Diamond cut structure
   */
  public createRemoveFacetCut(selectors: string[]): DiamondCut {
    return {
      facetAddress: '0x0000000000000000000000000000000000000000',
      action: FacetCutAction.Remove,
      functionSelectors: [...selectors]
    };
  }

  /**
   * Validate a diamond cut before execution
   * 
   * @param cuts - Array of diamond cuts to validate
   * @param currentFacets - Current facets (defaults to current deployment)
   * @returns Promise resolving to validation result
   */
  public async validateDiamondCut(
    cuts: DiamondCut[], 
    currentFacets?: FacetInfo[]
  ): Promise<DiamondCutValidationResult> {
    try {
      const facets = currentFacets || await this.listFacets();
      const errors: string[] = [];
      const warnings: string[] = [];
      let addedSelectors = 0;
      let replacedSelectors = 0;
      let removedSelectors = 0;

      // Build current selector map
      const selectorMap = new Map<string, FacetInfo>();
      for (const facet of facets) {
        for (const selector of facet.selectors) {
          selectorMap.set(selector, facet);
        }
      }

      for (const cut of cuts) {
        const { facetAddress, action, functionSelectors } = cut;

        // Validate facet address format
        if (action !== FacetCutAction.Remove && !/^0x[a-fA-F0-9]{40}$/.test(facetAddress)) {
          errors.push(`Invalid facet address format: ${facetAddress}`);
        }

        // Validate selector format
        for (const selector of functionSelectors) {
          if (!/^0x[a-fA-F0-9]{8}$/.test(selector)) {
            errors.push(`Invalid selector format: ${selector}`);
          }
        }

        switch (action) {
          case FacetCutAction.Add:
            addedSelectors += functionSelectors.length;

            // Check for conflicts with existing selectors
            for (const selector of functionSelectors) {
              if (selectorMap.has(selector)) {
                const existingFacet = selectorMap.get(selector)!;
                errors.push(`Selector ${selector} already exists in facet ${existingFacet.name}`);
              }
            }
            break;

          case FacetCutAction.Replace:
            replacedSelectors += functionSelectors.length;

            // Check that selectors exist to replace
            for (const selector of functionSelectors) {
              if (!selectorMap.has(selector)) {
                warnings.push(`Selector ${selector} does not exist to replace`);
              }
            }
            break;

          case FacetCutAction.Remove:
            removedSelectors += functionSelectors.length;

            // Check that selectors exist to remove
            for (const selector of functionSelectors) {
              if (!selectorMap.has(selector)) {
                errors.push(`Selector ${selector} does not exist to remove`);
              }
            }

            // Validate zero address for remove action
            if (facetAddress !== '0x0000000000000000000000000000000000000000') {
              errors.push(`Remove action must use zero address, got: ${facetAddress}`);
            }
            break;

          default:
            errors.push(`Invalid cut action: ${action}`);
        }
      }

      return {
        isValid: errors.length === 0,
        errors,
        warnings,
        summary: {
          addedSelectors,
          replacedSelectors,
          removedSelectors
        }
      };
    } catch (error) {
      throw new FacetManagerError('Failed to validate diamond cut', error as Error);
    }
  }
}

