 *
 * Provides comprehensive CLI options, error handling, and professional user experience
 */

import { task } from 'hardhat/config';
import { HardhatRuntimeEnvironment } from 'hardhat/types';
import * as fs from 'fs';
import * as path from 'path';
import chalk from 'chalk';

import {
  TaskArgs,
  DiamondInfo,
  MonitoringConfig,
  ReportFormat,
  MonitoringReport,
  MonitoringStatus,
} from '../core/types';
import { DiamondMonitoringSystem } from '../core/DiamondMonitoringSystem';
import { ReportGenerator } from '../reports/ReportGenerator';
import { ReportOptions } from '../reports/types';

/**
 * Extended task arguments with enhanced options
 */
interface EnhancedTaskArgs extends TaskArgs {
  targetNetwork?: string;
  outputFormat?: string;
  outputFile?: string;
  configPath?: string;
  deploymentPath?: string;
  debug?: boolean;
  failOnError?: boolean;
  watch?: boolean;
  dryRun?: boolean;
  parallel?: boolean;
  interval?: string;
}

task('monitor-diamond', 'Monitor ERC-2535 Diamond contract deployment')
  .addPositionalParam('diamondName', 'Name of the diamond contract to monitor')
  .addOptionalParam('targetNetwork', 'Target network (overrides --network flag)')
  .addOptionalParam('modules', 'Comma-separated list of modules to run', 'all')
  .addOptionalParam('outputFormat', 'Report format (console|json|html|markdown|csv)', 'console')
  .addOptionalParam('outputFile', 'Output file path for reports')
  .addOptionalParam('configPath', 'Path to custom configuration file')
  .addOptionalParam('deploymentPath', 'Path to diamond deployment files')
  .addOptionalParam('interval', 'Watch mode interval in seconds', '300')
  .addFlag('debug', 'Enable verbose logging and detailed output')
  .addFlag('failOnError', 'Exit with error code if monitoring fails')
  .addFlag('watch', 'Enable continuous monitoring mode')
  .addFlag('dryRun', 'Preview monitoring without actual execution')
  .addFlag('parallel', 'Enable parallel module execution')
  .setAction(async (taskArgs: EnhancedTaskArgs, hre: HardhatRuntimeEnvironment) => {
    const startTime = Date.now();

    try {
      // Print header
      console.log(chalk.blue.bold('\n Diamond Monitoring System'));
      console.log(chalk.blue('\n'));

      // Validate arguments
      await validateTaskArguments(taskArgs, hre);

      // Load configuration
      const config = await loadMonitoringConfiguration(taskArgs, hre);

      // Get diamond information
      const diamond = await loadDiamondInfo(taskArgs, hre);

      // Initialize monitoring system
      const monitoringSystem = new DiamondMonitoringSystem();

      // Register available modules
      await registerMonitoringModules(monitoringSystem, hre);

      // Setup progress tracking
      setupProgressTracking(monitoringSystem, taskArgs.debug);

      if (taskArgs.dryRun) {
        return await performDryRun(monitoringSystem, diamond, config, taskArgs);
      }

      if (taskArgs.watch) {
        return await runContinuousMonitoring(monitoringSystem, diamond, config, taskArgs, hre);
      } else {
        return await runSingleMonitoring(monitoringSystem, diamond, config, taskArgs, hre);
      }
    } catch (error) {
      const endTime = Date.now();
      const duration = endTime - startTime;

      console.error(chalk.red.bold(`\n Diamond monitoring failed after ${duration}ms:`));
      console.error(chalk.red(`   ${(error as Error).message}\n`));

      if (taskArgs.debug) {
        console.error(chalk.gray('Stack trace:'));
        console.error(chalk.gray((error as Error).stack));
      }

      if (taskArgs.failOnError) {
        process.exit(1);
      }

      throw error;
    }
  });

// ========================================
// Implementation Functions
// ========================================

/**
 * Validate task arguments and environment
 */
async function validateTaskArguments(
  taskArgs: EnhancedTaskArgs,
  hre: HardhatRuntimeEnvironment
): Promise<void> {
  if (taskArgs.debug) {
    console.log(chalk.blue(' Validating arguments and environment...'));
  }

  // Validate diamond name
  if (!taskArgs.diamondName || taskArgs.diamondName.trim() === '') {
    throw new Error('Diamond name is required');
  }

  // Validate output format
  const validFormats = ['console', 'json', 'html', 'markdown', 'csv'];
  if (taskArgs.outputFormat && !validFormats.includes(taskArgs.outputFormat)) {
    throw new Error(
      `Invalid output format '${taskArgs.outputFormat}'. Valid options: ${validFormats.join(', ')}`
    );
  }

  // Validate output file path if provided
  if (taskArgs.outputFile) {
    const dir = path.dirname(path.resolve(taskArgs.outputFile));
    if (!fs.existsSync(dir)) {
      throw new Error(`Output directory does not exist: ${dir}`);
    }
  }

  // Validate configuration file if provided
  if (taskArgs.configPath && !fs.existsSync(taskArgs.configPath)) {
    throw new Error(`Configuration file not found: ${taskArgs.configPath}`);
  }

  // Validate deployment path if provided
  if (taskArgs.deploymentPath && !fs.existsSync(taskArgs.deploymentPath)) {
    throw new Error(`Deployment path not found: ${taskArgs.deploymentPath}`);
  }

  // Validate watch interval
  if (taskArgs.interval) {
    const interval = parseInt(taskArgs.interval);
    if (isNaN(interval) || interval < 10) {
      throw new Error('Watch interval must be a number >= 10 seconds');
    }
  }

  // Validate network connectivity
  if (taskArgs.targetNetwork && !hre.config.networks[taskArgs.targetNetwork]) {
    throw new Error(`Network '${taskArgs.targetNetwork}' not configured in Hardhat config`);
  }

  if (taskArgs.debug) {
    console.log(chalk.green(' All arguments validated successfully'));
  }
}

/**
 * Load monitoring configuration from file or defaults
 */
async function loadMonitoringConfiguration(
  taskArgs: EnhancedTaskArgs,
  hre: HardhatRuntimeEnvironment
): Promise<MonitoringConfig> {
  if (taskArgs.debug) {
    console.log(chalk.blue('  Loading monitoring configuration...'));
  }

  let config: Partial<MonitoringConfig> = {};

  // Load from configuration file if provided
  if (taskArgs.configPath) {
    try {
      const configContent = fs.readFileSync(taskArgs.configPath, 'utf8');
      config = JSON.parse(configContent);
      if (taskArgs.debug) {
        console.log(chalk.green(` Loaded configuration from ${taskArgs.configPath}`));
      }
    } catch (error) {
      throw new Error(
        `Failed to load configuration from ${taskArgs.configPath}: ${(error as Error).message}`
      );
    }
  }

  // Parse modules list
  const modulesList =
    taskArgs.modules !== 'all'
      ? taskArgs.modules
          ?.split(',')
          .map(m => m.trim())
          .filter(m => m) || []
      : [];

  // Default modules to enable when 'all' is specified or no modules provided
  const defaultModules = [
    'function-selectors',
    'diamond-structure',
    'access-control',
    'token-supply',
    'erc165-compliance',
  ];
  const enableAllModules = taskArgs.modules === 'all' || !taskArgs.modules;

  // Create monitoring configuration
  const monitoringConfig: MonitoringConfig = {
    modules: config.modules || {},
    execution: {
      parallelExecution: taskArgs.parallel || config.execution?.parallelExecution || false,
      maxConcurrency: config.execution?.maxConcurrency || 3,
      timeoutMs: config.execution?.timeoutMs || 30000,
      failFast: taskArgs.failOnError || config.execution?.failFast || false,
    },
    reporting: {
      format: (taskArgs.outputFormat as ReportFormat) || 'console',
      outputPath: taskArgs.outputFile,
      verbose: taskArgs.debug || false,
      includeMetadata: config.reporting?.includeMetadata || true,
    },
    network: {
      name: taskArgs.targetNetwork || hre.network.name,
      chainId: 0, // Will be set when loading diamond info
      rpcUrl: '', // Will be set when loading diamond info
    },
    diamond: {} as DiamondInfo, // Will be set when loading diamond info
  };

  // Configure specific modules if provided
  if (enableAllModules) {
    // Enable all default modules
    defaultModules.forEach(moduleId => {
      monitoringConfig.modules[moduleId] = {
        enabled: true,
        config: config.modules?.[moduleId]?.config || {},
      };
    });
  } else if (modulesList.length > 0) {
    // Enable only specified modules
    modulesList.forEach(moduleId => {
      monitoringConfig.modules[moduleId] = {
        enabled: true,
        config: config.modules?.[moduleId]?.config || {},
      };
    });
  }

  if (taskArgs.debug) {
    console.log(chalk.green(' Monitoring configuration loaded'));
  }

  return monitoringConfig;
}

/**
 * Load diamond information from deployment files
 */
async function loadDiamondInfo(
  taskArgs: EnhancedTaskArgs,
  hre: HardhatRuntimeEnvironment
): Promise<DiamondInfo> {
  if (taskArgs.debug) {
    console.log(chalk.blue(' Loading diamond information...'));
  }

  const networkName = taskArgs.targetNetwork || hre.network.name;
  const deploymentPath =
    taskArgs.deploymentPath || path.join(hre.config.paths.root, 'deployments', networkName);

  // Try to find diamond deployment file
  const possiblePaths = [
    path.join(deploymentPath, `${taskArgs.diamondName}.json`),
    path.join(deploymentPath, `${taskArgs.diamondName}Diamond.json`),
    path.join(deploymentPath, 'Diamond.json'),
    path.join(
      hre.config.paths.root,
      'diamond-deployments',
      networkName,
      `${taskArgs.diamondName}.json`
    ),
    // Add diamonds directory structure support
    path.join(
      hre.config.paths.root,
      'diamonds',
      taskArgs.diamondName,
      'deployments',
      `${networkName}.json`
    ),
    path.join(
      hre.config.paths.root,
      'diamonds',
      taskArgs.diamondName,
      'deployments',
      `deployed_${networkName}.json`
    ),
    path.join(
      hre.config.paths.root,
      'diamonds',
      taskArgs.diamondName,
      `deployed_${networkName}.json`
    ),
  ];

  let diamondDeployment: any = null;
  let deploymentFilePath: string = '';

  for (const filePath of possiblePaths) {
    if (fs.existsSync(filePath)) {
      try {
        diamondDeployment = JSON.parse(fs.readFileSync(filePath, 'utf8'));
        deploymentFilePath = filePath;
        break;
      } catch (error) {
        if (taskArgs.debug) {
          console.log(chalk.yellow(`  Failed to parse ${filePath}: ${(error as Error).message}`));
        }
      }
    }
  }

  if (!diamondDeployment) {
    throw new Error(
      `Diamond deployment not found for '${taskArgs.diamondName}' on network '${networkName}'. Searched paths:\n${possiblePaths.join('\n')}`
    );
  }

  // Get network configuration
  const networkConfig = hre.config.networks[networkName];
  let rpcUrl = '';
  let chainId = 0;

  if (networkConfig && 'url' in networkConfig) {
    rpcUrl = networkConfig.url || '';
  }

  try {
    // Get chain ID from provider
    const ethers = (hre as any).ethers;
    if (!ethers || !ethers.provider) {
      throw new Error(
        'Ethers provider not available. Make sure @nomicfoundation/hardhat-ethers is installed.'
      );
    }

    const provider = ethers.provider;
    const network = await provider.getNetwork();
    chainId = Number(network.chainId);
  } catch (error) {
    if (taskArgs.debug) {
      console.log(chalk.yellow(`  Could not get chain ID: ${(error as Error).message}`));
    }
  }

  const diamond: DiamondInfo = {
    name: taskArgs.diamondName,
    address:
      diamondDeployment.address ||
      diamondDeployment.diamond?.address ||
      diamondDeployment.DiamondAddress,
    configPath: deploymentFilePath,
    deploymentBlock: diamondDeployment.receipt?.blockNumber,
    network: {
      name: networkName,
      chainId,
      rpcUrl,
    },
  };

  if (!diamond.address) {
    throw new Error(`Diamond address not found in deployment file: ${deploymentFilePath}`);
  }

  if (taskArgs.debug) {
    console.log(chalk.green(` Diamond loaded: ${diamond.name} at ${diamond.address}`));
    console.log(
      chalk.blue(`   Network: ${diamond.network.name} (Chain ID: ${diamond.network.chainId})`)
    );
  }

  return diamond;
}

/**
 * Register available monitoring modules
 */
async function registerMonitoringModules(
  system: DiamondMonitoringSystem,
  hre: HardhatRuntimeEnvironment
): Promise<void> {
  console.log(chalk.blue(' Registering monitoring modules...'));

  // Import and register all available monitoring modules
  const {
    FunctionSelectorModule,
    DiamondStructureModule,
    AccessControlModule,
    TokenSupplyModule,
    ERC165ComplianceModule,
  } = await import('../modules');

  const modules = [
    new FunctionSelectorModule(),
    new DiamondStructureModule(),
    new AccessControlModule(),
    new TokenSupplyModule(),
    new ERC165ComplianceModule(),
  ];

  // Register each module with the monitoring system
  modules.forEach(module => {
    system.registerModule(module);
  });

  console.log(chalk.green(' Monitoring modules registered'));
}

/**
 * Setup progress tracking for monitoring execution
 */
function setupProgressTracking(system: DiamondMonitoringSystem, verbose?: boolean): void {
  if (!verbose) return;

  console.log(chalk.blue(' Setting up progress tracking...'));

  let moduleCount = 0;
  let completedModules = 0;

  system.addEventListener(event => {
    switch (event.type) {
      case 'monitoring_start':
        console.log(chalk.blue(' Starting diamond monitoring...'));
        break;

      case 'module_start':
        moduleCount++;
        console.log(
          chalk.blue(`  [${completedModules + 1}/${moduleCount}] Starting: ${event.data.module}`)
        );
        break;

      case 'module_complete':
        completedModules++;
        console.log(
          chalk.green(` [${completedModules}/${moduleCount}] Completed: ${event.moduleId}`)
        );
        break;

      case 'module_error':
        completedModules++;
        console.log(chalk.red(` [${completedModules}/${moduleCount}] Failed: ${event.moduleId}`));
        console.log(chalk.red(`   Error: ${event.data.error}`));
        break;

      case 'issue_found':
        const issue = event.data.issue;
        const severityColor = getSeverityColor(issue.severity);
        console.log(severityColor(`    Issue found: ${issue.title} (${issue.severity})`));
        break;

      case 'monitoring_complete':
        console.log(chalk.green(' Diamond monitoring completed'));
        break;
    }
  });
}

/**
 * Get color function for severity level
 */
function getSeverityColor(severity: string): chalk.Chalk {
  switch (severity.toLowerCase()) {
    case 'critical':
      return chalk.red.bold;
    case 'error':
      return chalk.red;
    case 'warning':
      return chalk.yellow;
    case 'info':
      return chalk.blue;
    default:
      return chalk.gray;
  }
}

/**
 * Perform dry run to preview monitoring without execution
 */
async function performDryRun(
  system: DiamondMonitoringSystem,
  diamond: DiamondInfo,
  config: MonitoringConfig,
  taskArgs: EnhancedTaskArgs
): Promise<any> {
  console.log(chalk.yellow.bold('\n DRY RUN MODE - Preview Only\n'));

  console.log(chalk.blue(' Monitoring Preview:'));
  console.log(chalk.blue(`   Diamond: ${diamond.name} (${diamond.address})`));
  console.log(chalk.blue(`   Network: ${diamond.network.name} (${diamond.network.chainId})`));
  console.log(
    chalk.blue(`   Modules: ${Object.keys(config.modules).join(', ') || 'all available'}`)
  );
  console.log(
    chalk.blue(
      `   Output: ${config.reporting.format}${config.reporting.outputPath ? ` -> ${config.reporting.outputPath}` : ''}`
    )
  );

  const modules = system.listModules();
  console.log(chalk.blue(`\n Available Modules (${modules.length}):`));
  modules.forEach((module, index) => {
    console.log(chalk.blue(`   ${index + 1}. ${module.name} (${module.id})`));
  });

  console.log(chalk.yellow('\n Dry run completed - no actual monitoring performed'));

  return {
    mode: 'dry-run',
    diamond,
    config,
    availableModules: modules.length,
  };
}

/**
 * Run single monitoring execution
 */
async function runSingleMonitoring(
  system: DiamondMonitoringSystem,
  diamond: DiamondInfo,
  config: MonitoringConfig,
  taskArgs: EnhancedTaskArgs,
  hre: HardhatRuntimeEnvironment
): Promise<MonitoringReport> {
  console.log(chalk.blue(' Starting single monitoring execution...\n'));

  const provider = (hre as any).ethers.provider;
  const moduleIds =
    Object.keys(config.modules).length > 0 ? Object.keys(config.modules) : undefined;

  // Execute monitoring
  const report = await system.runMonitoring(diamond, provider, config, moduleIds);

  // Generate and save report
  if (taskArgs.outputFile || taskArgs.outputFormat !== 'console') {
    await generateAndSaveReport(report, taskArgs);
  }

  // Display summary
  displayMonitoringSummary(report, taskArgs);

  // Handle exit code
  if (taskArgs.failOnError && report.summary.failed > 0) {
    console.log(
      chalk.red.bold(`\n Monitoring failed with ${report.summary.failed} failed checks`)
    );
    process.exit(1);
  }

  return report;
}

/**
 * Run continuous monitoring with watch mode
 */
async function runContinuousMonitoring(
  system: DiamondMonitoringSystem,
  diamond: DiamondInfo,
  config: MonitoringConfig,
  taskArgs: EnhancedTaskArgs,
  hre: HardhatRuntimeEnvironment
): Promise<void> {
  const interval = parseInt(taskArgs.interval || '300') * 1000;

  console.log(chalk.yellow.bold('\n  CONTINUOUS MONITORING MODE'));
  console.log(chalk.yellow(`   Interval: ${interval / 1000} seconds`));
  console.log(chalk.yellow('   Press Ctrl+C to stop\n'));

  let runCount = 0;

  const runMonitoring = async () => {
    runCount++;
    console.log(chalk.blue(`\n[${new Date().toISOString()}] Monitoring run #${runCount}`));
    console.log(chalk.blue(''.repeat(60)));

    try {
      await runSingleMonitoring(system, diamond, config, taskArgs, hre);
    } catch (error) {
      console.error(
        chalk.red(` Monitoring run #${runCount} failed: ${(error as Error).message}`)
      );
    }
  };

  // Setup signal handlers for graceful shutdown
  process.on('SIGINT', () => {
    console.log(chalk.yellow('\n\n Stopping continuous monitoring...'));
    process.exit(0);
  });

  process.on('SIGTERM', () => {
    console.log(chalk.yellow('\n\n Stopping continuous monitoring...'));
    process.exit(0);
  });

  // Run initial monitoring
  await runMonitoring();

  // Setup interval for continuous monitoring
  setInterval(runMonitoring, interval);

  // Keep process alive
  return new Promise(() => {});
}

/**
 * Generate and save monitoring report
 */
async function generateAndSaveReport(
  report: MonitoringReport,
  taskArgs: EnhancedTaskArgs
): Promise<void> {
  if (!taskArgs.outputFormat || taskArgs.outputFormat === 'console') {
    return;
  }

  try {
    const format = taskArgs.outputFormat as ReportFormat;
    const outputPath = taskArgs.outputFile;

    const options: ReportOptions = {
      includeMetadata: true,
      includeDetails: taskArgs.debug,
      colorOutput: true,
      includeRecommendations: true,
    };

    await ReportGenerator.generateReport(report, format, outputPath, options);

    if (outputPath) {
      console.log(chalk.green(` Report saved: ${outputPath}`));
    }
  } catch (error) {
    console.error(chalk.red(` Failed to generate report: ${(error as Error).message}`));
  }
}

/**
 * Display monitoring summary
 */
function displayMonitoringSummary(report: MonitoringReport, taskArgs: EnhancedTaskArgs): void {
  console.log(chalk.blue('\n Monitoring Summary:'));
  console.log(chalk.blue(''.repeat(30)));

  const { summary } = report;

  // Status indicator
  let statusIcon = '';
  let statusColor = chalk.green;

  if (summary.failed > 0) {
    statusIcon = '';
    statusColor = chalk.red;
  } else if (summary.warnings > 0) {
    statusIcon = '';
    statusColor = chalk.yellow;
  }

  console.log(statusColor(`${statusIcon} Overall Status: ${summary.status}`));
  console.log(chalk.blue(` Total Checks: ${summary.totalChecks}`));
  console.log(chalk.green(` Passed: ${summary.passed}`));

  if (summary.failed > 0) {
    console.log(chalk.red(` Failed: ${summary.failed}`));
  }

  if (summary.warnings > 0) {
    console.log(chalk.yellow(`  Warnings: ${summary.warnings}`));
  }

  if (summary.skipped > 0) {
    console.log(chalk.gray(`  Skipped: ${summary.skipped}`));
  }

  console.log(chalk.blue(`  Duration: ${report.duration}ms`));

  // Module details
  if (taskArgs.debug && report.modules.length > 0) {
    console.log(chalk.blue('\n Module Results:'));
    report.modules.forEach(module => {
      const statusIcon = getModuleStatusIcon(module.status);
      const statusColor = getModuleStatusColor(module.status);
      console.log(statusColor(`   ${statusIcon} ${module.moduleName} (${module.duration}ms)`));

      if (module.result.issues.length > 0) {
        module.result.issues.forEach(issue => {
          const severityColor = getSeverityColor(issue.severity);
          console.log(severityColor(`       ${issue.title} (${issue.severity})`));
        });
      }
    });
  }
}

/**
 * Get status icon for module
 */
function getModuleStatusIcon(status: MonitoringStatus): string {
  switch (status) {
    case MonitoringStatus.PASS:
      return '';
    case MonitoringStatus.FAIL:
      return '';
    case MonitoringStatus.WARNING:
      return '';
    case MonitoringStatus.SKIPPED:
      return '';
    default:
      return '';
  }
}

/**
 * Get status color for module
 */
function getModuleStatusColor(status: MonitoringStatus): chalk.Chalk {
  switch (status) {
    case MonitoringStatus.PASS:
      return chalk.green;
    case MonitoringStatus.FAIL:
      return chalk.red;
    case MonitoringStatus.WARNING:
      return chalk.yellow;
    case MonitoringStatus.SKIPPED:
      return chalk.gray;
    default:
      return chalk.gray;
  }
}

/**
 * Core types and interfaces for the Hardhat Diamond Monitoring Plugin
 */

import { Provider } from 'ethers';
import { HardhatRuntimeEnvironment } from 'hardhat/types';

/**
 * Severity levels for monitoring issues
 */
export enum SeverityLevel {
  INFO = 'info',
  WARNING = 'warning',
  ERROR = 'error',
  CRITICAL = 'critical',
}

/**
 * Overall monitoring status
 */
export enum MonitoringStatus {
  PASS = 'PASS',
  FAIL = 'FAIL',
  WARNING = 'WARNING',
  SKIPPED = 'SKIPPED',
}

/**
 * Report output formats
 */
export enum ReportFormat {
  CONSOLE = 'console',
  JSON = 'json',
  HTML = 'html',
  CSV = 'csv',
}

/**
 * Network information for monitoring context
 */
export interface NetworkInfo {
  name: string;
  chainId: number;
  rpcUrl: string;
  blockExplorerUrl?: string;
  blockExplorerApiKey?: string;
}

/**
 * Diamond information for monitoring
 */
export interface DiamondInfo {
  name: string;
  address: string;
  configPath?: string;
  deploymentBlock?: number;
  network: NetworkInfo;
}

/**
 * Configuration validation result
 */
export interface ConfigValidationResult {
  isValid: boolean;
  errors: string[];
  warnings: string[];
}

/**
 * Configuration requirements for monitoring modules
 */
export interface ConfigRequirement {
  key: string;
  type: 'string' | 'number' | 'boolean' | 'object' | 'array';
  required: boolean;
  description: string;
  defaultValue?: any;
  validation?: (value: any) => boolean;
}

/**
 * Configuration validation result
 */
export interface ValidationResult {
  isValid: boolean;
  errors: string[];
  warnings: string[];
}

/**
 * Individual monitoring issue
 */
export interface MonitoringIssue {
  id: string;
  title: string;
  description: string;
  severity: SeverityLevel;
  category: string;
  recommendation?: string;
  metadata?: Record<string, any>;
  location?: {
    contract?: string;
    function?: string;
    line?: number;
  };
}

/**
 * Result from executing a monitoring module
 */
export interface MonitoringResult {
  status: MonitoringStatus;
  issues: MonitoringIssue[];
  executionTime: number;
  metadata?: Record<string, any>;
}

/**
 * Context provided to monitoring modules during execution
 */
export interface MonitoringContext {
  diamond: DiamondInfo;
  provider: Provider;
  config: MonitoringConfig;
  moduleConfig: Record<string, any>;
  hre: HardhatRuntimeEnvironment;
  dryRun?: boolean;
  verbose?: boolean;
}

/**
 * Result from a module execution
 */
export interface ModuleResult {
  moduleId: string;
  moduleName: string;
  status: MonitoringStatus;
  result: MonitoringResult;
  startTime: Date;
  endTime: Date;
  duration: number;
  error?: Error;
}

/**
 * Complete monitoring report
 */
export interface MonitoringReport {
  summary: {
    status: MonitoringStatus;
    totalChecks: number;
    passed: number;
    failed: number;
    warnings: number;
    skipped: number;
  };
  modules: ModuleResult[];
  diamond: DiamondInfo;
  network: NetworkInfo;
  config: MonitoringConfig;
  timestamp: Date;
  duration: number;
  recommendations?: string[];
}

/**
 * Monitoring module interface
 */
export interface MonitoringModule {
  readonly id: string;
  readonly name: string;
  readonly description: string;
  readonly version: string;
  readonly category: string;

  /**
   * Check if this module can monitor the given diamond
   */
  canMonitor(diamond: DiamondInfo, network: NetworkInfo): Promise<boolean>;

  /**
   * Get configuration requirements for this module
   */
  getRequiredConfig(): ConfigRequirement[];

  /**
   * Validate module-specific configuration
   */
  validateConfig(config: any): ValidationResult;

  /**
   * Execute monitoring
   */
  monitor(context: MonitoringContext): Promise<MonitoringResult>;

  /**
   * Optional cleanup after monitoring
   */
  cleanup?(context: MonitoringContext): Promise<void>;
}

/**
 * Main monitoring configuration
 */
export interface MonitoringConfig {
  // Module configuration
  modules: Record<
    string,
    {
      enabled: boolean;
      priority?: number;
      config?: Record<string, any>;
    }
  >;

  // Execution configuration
  execution: {
    parallelExecution: boolean;
    maxConcurrency: number;
    timeoutMs: number;
    failFast: boolean;
  };

  // Reporting configuration
  reporting: {
    format: ReportFormat;
    outputPath?: string;
    verbose: boolean;
    includeMetadata: boolean;
  };

  // Network configuration
  network: NetworkInfo;

  // Diamond configuration
  diamond: DiamondInfo;
}

/**
 * Task arguments interface
 */
export interface TaskArgs {
  diamondName: string;
  network?: string; // Optional since we derive from Hardhat context
  modules?: string;
  outputFormat?: string;
  outputFile?: string;
  verbose?: boolean;
  failOnError?: boolean;
  dryRun?: boolean;
}

/**
 * Monitoring event types
 */
export type MonitoringEventType =
  | 'monitoring_start'
  | 'monitoring_complete'
  | 'module_start'
  | 'module_complete'
  | 'module_error'
  | 'issue_found';

/**
 * Monitoring event data
 */
export interface MonitoringEvent {
  type: MonitoringEventType;
  timestamp: Date;
  moduleId?: string;
  data: Record<string, any>;
}

/**
 * Monitoring event listener
 */
export type MonitoringEventListener = (event: MonitoringEvent) => void;

/**
 * Logger interface for monitoring system
 */
export interface Logger {
  debug(message: string, ...args: any[]): void;
  info(message: string, ...args: any[]): void;
  warn(message: string, ...args: any[]): void;
  error(message: string, ...args: any[]): void;
}

/**
 * Retry configuration for network operations
 */
export interface RetryConfig {
  maxAttempts: number;
  baseDelayMs: number;
  maxDelayMs: number;
  backoffMultiplier: number;
}

/**
 * Module dependency information
 */
export interface ModuleDependency {
  moduleId: string;
  optional: boolean;
}

/**
 * Extended monitoring module interface with dependencies
 */
export interface MonitoringModuleWithDeps extends MonitoringModule {
  /**
   * Get module dependencies (optional)
   */
  getDependencies?(): ModuleDependency[];
}

/**
 * Diamond configuration from deployment files
 */
export interface DiamondConfig {
  name: string;
  address: string;
  deploymentBlock?: number;
  facets: Record<
    string,
    {
      address: string;
      selectors: string[];
    }
  >;
  protocolVersion?: string;
  metadata?: Record<string, any>;
}

/**
 * Main Diamond Monitoring System for Hardhat Plugin
 *
 * Orchestrates monitoring modules and manages the overall monitoring process
 * within the Hardhat environment.
 */

import { Provider } from 'ethers';
import chalk from 'chalk';

import {
  MonitoringModule,
  MonitoringConfig,
  MonitoringReport,
  MonitoringContext,
  MonitoringStatus,
  ModuleResult,
  DiamondInfo,
  NetworkInfo,
  MonitoringEvent,
  MonitoringEventListener,
  SeverityLevel,
  Logger,
  RetryConfig,
  ModuleDependency,
  MonitoringModuleWithDeps,
} from './types';

/**
 * Main orchestration engine for diamond monitoring
 */
export class DiamondMonitoringSystem {
  private modules: Map<string, MonitoringModule> = new Map();
  private eventListeners: MonitoringEventListener[] = [];
  private logger: Logger;
  private readonly version = '1.0.0';
  private connectionPool: Map<string, Provider> = new Map();
  private readonly defaultRetryConfig: RetryConfig = {
    maxAttempts: 3,
    baseDelayMs: 1000,
    maxDelayMs: 10000,
    backoffMultiplier: 2,
  };

  /**
   * Initialize the monitoring system
   * @param logger - Optional logger instance, defaults to console logger
   */
  constructor(logger?: Logger) {
    this.logger = logger || this.createDefaultLogger();
  }

  /**
   * Create a default console logger
   */
  private createDefaultLogger(): Logger {
    return {
      debug: (message: string, ...args: any[]) => {
        console.debug(chalk.gray(`[DEBUG] ${message}`), ...args);
      },
      info: (message: string, ...args: any[]) => {
        console.info(chalk.blue(`[INFO] ${message}`), ...args);
      },
      warn: (message: string, ...args: any[]) => {
        console.warn(chalk.yellow(`[WARN] ${message}`), ...args);
      },
      error: (message: string, ...args: any[]) => {
        console.error(chalk.red(`[ERROR] ${message}`), ...args);
      },
    };
  }

  /**
   * Register a monitoring module
   * @param module - The monitoring module to register
   * @throws Error if module ID is already registered
   */
  public registerModule(module: MonitoringModule): void {
    if (this.modules.has(module.id)) {
      const error = `Module with ID '${module.id}' is already registered`;
      this.logger.error(error);
      throw new Error(error);
    }

    this.modules.set(module.id, module);
    this.logger.info(`Registered module: ${module.name} (${module.id})`);
  }

  /**
   * Unregister a monitoring module
   * @param moduleId - The ID of the module to unregister
   * @returns true if module was unregistered, false if module was not found
   */
  public unregisterModule(moduleId: string): boolean {
    if (!this.modules.has(moduleId)) {
      this.logger.warn(`Module with ID '${moduleId}' is not registered`);
      return false;
    }

    this.modules.delete(moduleId);
    this.logger.info(`Unregistered module: ${moduleId}`);
    return true;
  }

  /**
   * List all registered modules
   * @returns Array of all registered monitoring modules
   */
  public listModules(): MonitoringModule[] {
    return Array.from(this.modules.values());
  }

  /**
   * Get a specific module by ID
   * @param moduleId - The ID of the module to retrieve
   * @returns The module if found, undefined otherwise
   */
  public getModule(moduleId: string): MonitoringModule | undefined {
    return this.modules.get(moduleId);
  }

  /**
   * Add event listener for monitoring events
   * @param listener - Event listener function
   */
  public addEventListener(listener: MonitoringEventListener): void {
    this.eventListeners.push(listener);
    this.logger.debug('Added event listener');
  }

  /**
   * Remove event listener
   * @param listener - Event listener function to remove
   */
  public removeEventListener(listener: MonitoringEventListener): void {
    const index = this.eventListeners.indexOf(listener);
    if (index !== -1) {
      this.eventListeners.splice(index, 1);
      this.logger.debug('Removed event listener');
    }
  }

  /**
   * Emit event to all listeners
   * @param event - The monitoring event to emit
   */
  public emitEvent(event: MonitoringEvent): void {
    this.eventListeners.forEach(listener => {
      try {
        listener(event);
      } catch (error) {
        this.logger.error(`Error in event listener: ${(error as Error).message}`);
      }
    });
  }

  /**
   * Run monitoring with specified configuration
   * @param diamond - Diamond information
   * @param provider - Ethereum provider
   * @param config - Monitoring configuration
   * @param moduleIds - Optional array of specific module IDs to run
   * @param reportCallback - Optional callback for progress reporting
   * @returns Complete monitoring report
   */
  public async runMonitoring(
    diamond: DiamondInfo,
    provider: Provider,
    config: MonitoringConfig,
    moduleIds?: string[],
    reportCallback?: (report: MonitoringReport) => void
  ): Promise<MonitoringReport> {
    const startTime = Date.now();

    if (config.reporting.verbose) {
      this.logger.info(' Starting Diamond Monitoring System');
      this.logger.info(` Diamond: ${diamond.name} (${diamond.address})`);
      this.logger.info(
        ` Network: ${diamond.network.name} (Chain ID: ${diamond.network.chainId})`
      );
    }

    this.emitEvent({
      type: 'monitoring_start',
      timestamp: new Date(),
      data: { diamond, config },
    });

    try {
      // Get modules to run based on configuration and optional filter
      const modulesToRun = this.getModulesToRun(config, moduleIds);

      if (modulesToRun.length === 0) {
        const error = moduleIds
          ? `No specified modules found: ${moduleIds.join(', ')}`
          : 'No monitoring modules configured to run';
        this.logger.error(error);
        throw new Error(error);
      }

      // Order modules by dependencies
      const orderedModules = this.orderModulesByDependencies(modulesToRun);

      // Validate configurations for all modules
      await this.validateModuleConfigurations(orderedModules, {
        diamond,
        provider: this.getOrCreateProvider(provider, diamond.network),
        config,
        moduleConfig: {},
        hre: (config as any).hre,
      } as MonitoringContext);

      if (config.reporting.verbose) {
        this.logger.info(` Running ${orderedModules.length} monitoring modules:`);
        orderedModules.forEach(module => {
          this.logger.info(`    ${module.name} (${module.id})`);
        });
      }

      // Create monitoring context
      const context: MonitoringContext = {
        diamond,
        provider,
        config,
        moduleConfig: config.modules || {},
        hre: (config as any).hre,
        verbose: config.reporting.verbose,
      };

      // Execute modules
      const moduleResults = config.execution.parallelExecution
        ? await this.runModulesParallel(modulesToRun, context, config.execution.maxConcurrency)
        : await this.runModulesSequential(modulesToRun, context);

      // Generate final report
      const endTime = Date.now();
      const report = this.generateReport(moduleResults, diamond, config, startTime, endTime);

      this.emitEvent({
        type: 'monitoring_complete',
        timestamp: new Date(),
        data: { report },
      });

      if (config.reporting.verbose) {
        this.logger.info(` Diamond monitoring completed in ${endTime - startTime}ms`);
      }

      if (reportCallback) {
        reportCallback(report);
      }

      return report;
    } catch (error) {
      const endTime = Date.now();
      const errorMessage = (error as Error).message;

      this.logger.error(` Diamond monitoring failed: ${errorMessage}`, {
        diamond: diamond.name,
        network: diamond.network.name,
        duration: endTime - startTime,
        error: error as Error,
      });

      if (config.reporting.verbose) {
        this.logger.error(` Diamond monitoring failed: ${errorMessage}`);
      }

      const report: MonitoringReport = {
        summary: {
          status: MonitoringStatus.FAIL,
          totalChecks: 0,
          passed: 0,
          failed: 1,
          warnings: 0,
          skipped: 0,
        },
        modules: [],
        diamond,
        network: diamond.network,
        config,
        timestamp: new Date(startTime),
        duration: endTime - startTime,
      };

      return report;
    }
  }

  /**
   * Validate configurations for all modules before execution
   * @param modules - Array of modules to validate
   * @param context - Monitoring context
   */
  private async validateModuleConfigurations(
    modules: MonitoringModule[],
    context: MonitoringContext
  ): Promise<void> {
    if (context.verbose) {
      this.logger.info('\n Validating module configurations...');
    }

    for (const module of modules) {
      const moduleConfig = context.moduleConfig[module.id] || {};
      const validation = module.validateConfig(moduleConfig);

      if (!validation.isValid) {
        this.logger.error(` Configuration validation failed for module '${module.id}':`);
        validation.errors.forEach(error => this.logger.error(`    ${error}`));
        throw new Error(`Module '${module.id}' configuration validation failed`);
      }

      if (validation.warnings.length > 0 && context.verbose) {
        this.logger.warn(`  Configuration warnings for module '${module.id}':`);
        validation.warnings.forEach(warning => this.logger.warn(`    ${warning}`));
      }
    }
  }

  /**
   * Get modules to run based on configuration and optional module filter
   * @param config - Monitoring configuration
   * @param moduleIds - Optional array of specific module IDs to run
   * @returns Array of modules to execute
   */
  private getModulesToRun(config: MonitoringConfig, moduleIds?: string[]): MonitoringModule[] {
    const availableModules = Array.from(this.modules.values());

    // Filter by specific module IDs if provided
    const filteredModules = moduleIds
      ? availableModules.filter(module => moduleIds.includes(module.id))
      : availableModules;

    // Filter modules based on configuration
    const enabledModules = filteredModules.filter(module => {
      const moduleConfig = config.modules[module.id];
      return moduleConfig && moduleConfig.enabled !== false;
    });

    // Sort by priority if specified
    enabledModules.sort((a, b) => {
      const priorityA = config.modules[a.id]?.priority || 0;
      const priorityB = config.modules[b.id]?.priority || 0;
      return priorityA - priorityB;
    });

    return enabledModules;
  }

  /**
   * Order modules by their dependencies using topological sort
   * @param modules - Array of modules to order
   * @returns Modules ordered by dependencies
   */
  private orderModulesByDependencies(modules: MonitoringModule[]): MonitoringModule[] {
    const moduleMap = new Map<string, MonitoringModule>();
    const dependencyMap = new Map<string, string[]>();
    const inDegree = new Map<string, number>();

    // Initialize maps
    modules.forEach(module => {
      moduleMap.set(module.id, module);
      inDegree.set(module.id, 0);

      // Get dependencies if module supports them
      const moduleWithDeps = module as MonitoringModuleWithDeps;
      if (moduleWithDeps.getDependencies) {
        const dependencies = moduleWithDeps
          .getDependencies()
          .filter(dep => moduleMap.has(dep.moduleId))
          .map(dep => dep.moduleId);
        dependencyMap.set(module.id, dependencies);
      } else {
        dependencyMap.set(module.id, []);
      }
    });

    // Calculate in-degrees
    dependencyMap.forEach((dependencies, moduleId) => {
      dependencies.forEach(depId => {
        if (inDegree.has(depId)) {
          inDegree.set(moduleId, (inDegree.get(moduleId) || 0) + 1);
        }
      });
    });

    // Topological sort
    const result: MonitoringModule[] = [];
    const queue: string[] = [];

    // Find modules with no dependencies
    inDegree.forEach((degree, moduleId) => {
      if (degree === 0) {
        queue.push(moduleId);
      }
    });

    while (queue.length > 0) {
      const moduleId = queue.shift()!;
      const module = moduleMap.get(moduleId)!;
      result.push(module);

      // Update in-degrees for dependent modules
      modules.forEach(m => {
        const deps = dependencyMap.get(m.id) || [];
        if (deps.includes(moduleId)) {
          const newDegree = (inDegree.get(m.id) || 0) - 1;
          inDegree.set(m.id, newDegree);
          if (newDegree === 0) {
            queue.push(m.id);
          }
        }
      });
    }

    // If not all modules were processed, there's a circular dependency
    if (result.length !== modules.length) {
      this.logger.warn('Circular dependency detected in modules, using original order');
      return modules;
    }

    return result;
  }

  /**
   * Get or create a provider with connection reuse
   * @param provider - Original provider
   * @param network - Network information
   * @returns Provider instance (reused if possible)
   */
  private getOrCreateProvider(provider: Provider, network: NetworkInfo): Provider {
    const cacheKey = `${network.name}-${network.chainId}`;

    if (this.connectionPool.has(cacheKey)) {
      this.logger.debug(`Reusing provider for ${network.name}`);
      return this.connectionPool.get(cacheKey)!;
    }

    this.connectionPool.set(cacheKey, provider);
    this.logger.debug(`Created new provider for ${network.name}`);
    return provider;
  }

  /**
   * Execute operation with exponential backoff retry
   * @param operation - Async operation to retry
   * @param operationName - Name for logging
   * @param retryConfig - Retry configuration
   * @returns Operation result
   */
  private async executeWithRetry<T>(
    operation: () => Promise<T>,
    operationName: string,
    retryConfig: RetryConfig = this.defaultRetryConfig
  ): Promise<T> {
    let lastError: Error;

    for (let attempt = 1; attempt <= retryConfig.maxAttempts; attempt++) {
      try {
        return await operation();
      } catch (error) {
        lastError = error as Error;

        if (attempt === retryConfig.maxAttempts) {
          this.logger.error(
            `${operationName} failed after ${attempt} attempts: ${lastError.message}`
          );
          throw lastError;
        }

        const delay = Math.min(
          retryConfig.baseDelayMs * Math.pow(retryConfig.backoffMultiplier, attempt - 1),
          retryConfig.maxDelayMs
        );

        this.logger.warn(
          `${operationName} failed (attempt ${attempt}/${retryConfig.maxAttempts}), retrying in ${delay}ms: ${lastError.message}`
        );
        await this.sleep(delay);
      }
    }

    throw lastError!;
  }

  /**
   * Sleep for specified milliseconds
   * @param ms - Milliseconds to sleep
   */
  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Run modules in parallel
   */
  private async runModulesParallel(
    modules: MonitoringModule[],
    context: MonitoringContext,
    maxConcurrency: number
  ): Promise<ModuleResult[]> {
    const results: ModuleResult[] = [];
    const semaphore = new Array(maxConcurrency).fill(null);

    const promises = modules.map(async (module, index) => {
      // Wait for available slot
      const slotIndex = index % maxConcurrency;
      await semaphore[slotIndex];

      const result = await this.runSingleModule(module, context);
      results.push(result);
      return result;
    });

    await Promise.all(promises);
    return results.sort((a, b) => a.startTime.getTime() - b.startTime.getTime());
  }

  /**
   * Run modules sequentially
   */
  private async runModulesSequential(
    modules: MonitoringModule[],
    context: MonitoringContext
  ): Promise<ModuleResult[]> {
    const results: ModuleResult[] = [];

    for (const module of modules) {
      const result = await this.runSingleModule(module, context);
      results.push(result);

      // Check fail-fast configuration
      if (context.config.execution.failFast && result.status === MonitoringStatus.FAIL) {
        break;
      }
    }

    return results;
  }

  /**
   * Run a single monitoring module with retry logic and enhanced error handling
   * @param module - Module to execute
   * @param context - Monitoring context
   * @returns Module execution result
   */
  private async runSingleModule(
    module: MonitoringModule,
    context: MonitoringContext
  ): Promise<ModuleResult> {
    const startTime = new Date();

    this.emitEvent({
      type: 'module_start',
      timestamp: startTime,
      moduleId: module.id,
      data: { module: module.name },
    });

    if (context.verbose) {
      this.logger.info(`\n Running ${module.name}...`);
    }

    try {
      // Check if module can monitor this diamond with retry
      const canMonitor = await this.executeWithRetry(
        () => module.canMonitor(context.diamond, context.diamond.network),
        `${module.name} canMonitor check`
      );

      if (!canMonitor) {
        const endTime = new Date();
        const result: ModuleResult = {
          moduleId: module.id,
          moduleName: module.name,
          status: MonitoringStatus.SKIPPED,
          result: {
            status: MonitoringStatus.SKIPPED,
            issues: [],
            executionTime: endTime.getTime() - startTime.getTime(),
            metadata: { reason: 'Module cannot monitor this diamond' },
          },
          startTime,
          endTime,
          duration: endTime.getTime() - startTime.getTime(),
        };

        if (context.verbose) {
          this.logger.info(`  Skipped ${module.name} (cannot monitor this diamond)`);
        }

        return result;
      }

      // Create module-specific context
      const moduleContext: MonitoringContext = {
        ...context,
        moduleConfig: context.moduleConfig[module.id] || {},
      };

      // Execute module monitoring with retry and timeout
      const moduleResult = await Promise.race([
        this.executeWithRetry(() => module.monitor(moduleContext), `${module.name} monitoring`),
        this.createTimeoutPromise(context.config.execution.timeoutMs, module.name),
      ]);

      const endTime = new Date();
      const result: ModuleResult = {
        moduleId: module.id,
        moduleName: module.name,
        status: moduleResult.status,
        result: moduleResult,
        startTime,
        endTime,
        duration: endTime.getTime() - startTime.getTime(),
      };

      // Handle module issues
      if (moduleResult.issues.length > 0) {
        moduleResult.issues.forEach(issue => {
          this.emitEvent({
            type: 'issue_found',
            timestamp: new Date(),
            moduleId: module.id,
            data: { issue },
          });
        });
      }

      if (context.verbose) {
        const statusIcon = this.getStatusIcon(moduleResult.status);
        this.logger.info(
          `${statusIcon} ${module.name} completed in ${moduleResult.executionTime}ms`
        );

        if (moduleResult.issues.length > 0) {
          this.logger.warn(`   Found ${moduleResult.issues.length} issue(s)`);
        }
      }

      this.emitEvent({
        type: 'module_complete',
        timestamp: endTime,
        moduleId: module.id,
        data: { result: moduleResult },
      });

      return result;
    } catch (error) {
      const endTime = new Date();
      const errorMessage = (error as Error).message;

      this.logger.error(`Module '${module.name}' execution failed: ${errorMessage}`, {
        moduleId: module.id,
        duration: endTime.getTime() - startTime.getTime(),
        error: error as Error,
      });

      const errorResult: ModuleResult = {
        moduleId: module.id,
        moduleName: module.name,
        status: MonitoringStatus.FAIL,
        result: {
          status: MonitoringStatus.FAIL,
          issues: [
            {
              id: 'module-error',
              title: 'Module Execution Error',
              description: `Module '${module.name}' failed: ${errorMessage}`,
              severity: SeverityLevel.CRITICAL,
              category: 'system',
              metadata: {
                stackTrace: (error as Error).stack,
                timestamp: endTime.toISOString(),
              },
            },
          ],
          executionTime: endTime.getTime() - startTime.getTime(),
          metadata: { error: errorMessage },
        },
        startTime,
        endTime,
        duration: endTime.getTime() - startTime.getTime(),
        error: error as Error,
      };

      if (context.verbose) {
        this.logger.error(` ${module.name} failed: ${errorMessage}`);
      }

      this.emitEvent({
        type: 'module_error',
        timestamp: endTime,
        moduleId: module.id,
        data: { error: errorMessage },
      });

      return errorResult;
    } finally {
      // Cleanup if module supports it
      try {
        if (module.cleanup) {
          await module.cleanup(context);
        }
      } catch (cleanupError) {
        this.logger.warn(
          `Cleanup failed for module '${module.name}': ${(cleanupError as Error).message}`
        );
      }
    }
  }

  /**
   * Create a timeout promise for module execution
   */
  private createTimeoutPromise(timeoutMs: number, moduleName: string): Promise<never> {
    return new Promise((_, reject) => {
      setTimeout(() => {
        reject(new Error(`Module '${moduleName}' timed out after ${timeoutMs}ms`));
      }, timeoutMs);
    });
  }

  /**
   * Generate final monitoring report
   */
  private generateReport(
    moduleResults: ModuleResult[],
    diamond: DiamondInfo,
    config: MonitoringConfig,
    startTime: number,
    endTime: number
  ): MonitoringReport {
    const totalChecks = moduleResults.reduce(
      (sum, result) => sum + (result.result.issues?.length || 0) + 1,
      0
    );
    const passed = moduleResults.filter(r => r.status === MonitoringStatus.PASS).length;
    const failed = moduleResults.filter(r => r.status === MonitoringStatus.FAIL).length;
    const warnings = moduleResults.filter(r => r.status === MonitoringStatus.WARNING).length;
    const skipped = moduleResults.filter(r => r.status === MonitoringStatus.SKIPPED).length;

    // Determine overall status
    let overallStatus = MonitoringStatus.PASS;
    if (failed > 0) {
      overallStatus = MonitoringStatus.FAIL;
    } else if (warnings > 0) {
      overallStatus = MonitoringStatus.WARNING;
    }

    return {
      summary: {
        status: overallStatus,
        totalChecks,
        passed,
        failed,
        warnings,
        skipped,
      },
      modules: moduleResults,
      diamond,
      network: diamond.network,
      config,
      timestamp: new Date(startTime),
      duration: endTime - startTime,
    };
  }

  /**
   * Get status icon for display
   */
  private getStatusIcon(status: MonitoringStatus): string {
    switch (status) {
      case MonitoringStatus.PASS:
        return '';
      case MonitoringStatus.FAIL:
        return '';
      case MonitoringStatus.WARNING:
        return '';
      case MonitoringStatus.SKIPPED:
        return '';
      default:
        return '';
    }
  }

  /**
   * Get system version
   * @returns System version string
   */
  public getVersion(): string {
    return this.version;
  }

  /**
   * Get system statistics
   * @returns System statistics
   */
  public getStatistics(): {
    registeredModules: number;
    activeListeners: number;
    connectionPoolSize: number;
  } {
    return {
      registeredModules: this.modules.size,
      activeListeners: this.eventListeners.length,
      connectionPoolSize: this.connectionPool.size,
    };
  }

  /**
   * Clean up connection pool
   */
  public cleanupConnections(): void {
    this.connectionPool.clear();
    this.logger.debug('Connection pool cleared');
  }

  /**
   * Set custom logger
   * @param logger - Logger implementation
   */
  public setLogger(logger: Logger): void {
    this.logger = logger;
  }

  /**
   * Clear all modules and listeners (for testing)
   */
  public reset(): void {
    this.modules.clear();
    this.eventListeners.length = 0;
    this.connectionPool.clear();
    this.logger.debug('DiamondMonitoringSystem reset');
  }
}

/**
 * Main Report Generator for Diamond Monitoring System
 *
 * Orchestrates report generation across multiple formats with professional styling
 * and comprehensive output options.
 */

import * as fs from 'fs/promises';
import * as path from 'path';
import { MonitoringReport } from '../core/types';
import {
  ReportFormat,
  ReportOptions,
  ReportFormatter,
  ReportGenerationResult,
  ReportValidationResult,
} from './types';

// Import formatters
import { ConsoleFormatter } from './formatters/ConsoleFormatter';
import { JSONFormatter } from './formatters/JSONFormatter';
import { HTMLFormatter } from './formatters/HTMLFormatter';
import { MarkdownFormatter } from './formatters/MarkdownFormatter';
import { CSVFormatter } from './formatters/CSVFormatter';

/**
 * Main report generation orchestrator
 */
export class ReportGenerator {
  private static formatters: Map<ReportFormat, ReportFormatter> = new Map();

  static {
    // Register all formatters
    this.registerFormatter(ReportFormat.CONSOLE, new ConsoleFormatter());
    this.registerFormatter(ReportFormat.JSON, new JSONFormatter());
    this.registerFormatter(ReportFormat.HTML, new HTMLFormatter());
    this.registerFormatter(ReportFormat.MARKDOWN, new MarkdownFormatter());
    this.registerFormatter(ReportFormat.CSV, new CSVFormatter());
  }

  /**
   * Generate a report in the specified format
   * @param report - The monitoring report to format
   * @param format - The desired output format
   * @param outputPath - Optional file path to save the report
   * @param options - Report generation options
   * @returns The generation result
   */
  public static async generateReport(
    report: MonitoringReport,
    format: ReportFormat,
    outputPath?: string,
    options?: ReportOptions
  ): Promise<ReportGenerationResult> {
    const startTime = Date.now();
    const warnings: string[] = [];

    try {
      // Get the appropriate formatter
      const formatter = this.getFormatter(format);
      if (!formatter) {
        throw new Error(`Unsupported report format: ${format}`);
      }

      // Validate options
      const validation = formatter.validateOptions(options);
      if (!validation.isValid) {
        throw new Error(`Invalid options for ${format} format: ${validation.errors.join(', ')}`);
      }
      warnings.push(...validation.warnings);

      // Apply default options
      const mergedOptions = this.mergeDefaultOptions(options, format);

      // Generate the report content
      const content = await formatter.format(report, mergedOptions);

      // Determine output path if saving
      let finalOutputPath: string | undefined;
      let fileSize: number | undefined;

      if (outputPath) {
        finalOutputPath = this.resolveOutputPath(outputPath, format, mergedOptions);

        // Ensure directory exists
        const dir = path.dirname(finalOutputPath);
        await fs.mkdir(dir, { recursive: true });

        // Save the report
        await this.saveReport(content, finalOutputPath, format);

        // Get file size
        const stats = await fs.stat(finalOutputPath);
        fileSize = stats.size;
      }

      const duration = Date.now() - startTime;

      return {
        success: true,
        content,
        filePath: finalOutputPath,
        duration,
        fileSize,
        warnings,
      };
    } catch (error) {
      const duration = Date.now() - startTime;

      return {
        success: false,
        duration,
        error: (error as Error).message,
        warnings,
      };
    }
  }

  /**
   * Save report content to file
   * @param content - The report content
   * @param outputPath - File path to save to
   * @param format - Report format for proper encoding
   */
  public static async saveReport(
    content: string,
    outputPath: string,
    format: ReportFormat
  ): Promise<void> {
    try {
      // Ensure directory exists
      const dir = path.dirname(outputPath);
      await fs.mkdir(dir, { recursive: true });

      // Write the file with appropriate encoding
      await fs.writeFile(outputPath, content, 'utf8');

      // Set appropriate permissions
      await fs.chmod(outputPath, 0o644);
    } catch (error) {
      throw new Error(`Failed to save report to ${outputPath}: ${(error as Error).message}`);
    }
  }

  /**
   * Generate multiple reports in different formats
   * @param report - The monitoring report
   * @param formats - Array of formats to generate
   * @param outputDir - Directory to save reports
   * @param options - Report generation options
   * @returns Array of generation results
   */
  public static async generateMultipleReports(
    report: MonitoringReport,
    formats: ReportFormat[],
    outputDir?: string,
    options?: ReportOptions
  ): Promise<ReportGenerationResult[]> {
    const results: ReportGenerationResult[] = [];

    for (const format of formats) {
      const outputPath = outputDir
        ? path.join(outputDir, this.getDefaultFilename(report, format, options))
        : undefined;

      const result = await this.generateReport(report, format, outputPath, options);
      results.push(result);
    }

    return results;
  }

  /**
   * Get available formatters
   * @returns Array of available format types
   */
  public static getAvailableFormats(): ReportFormat[] {
    return Array.from(this.formatters.keys());
  }

  /**
   * Get formatter information
   * @param format - The format to get info for
   * @returns Formatter information
   */
  public static getFormatterInfo(
    format: ReportFormat
  ): Pick<ReportFormatter, 'id' | 'name' | 'extension' | 'mimeType'> | null {
    const formatter = this.formatters.get(format);
    if (!formatter) return null;

    return {
      id: formatter.id,
      name: formatter.name,
      extension: formatter.extension,
      mimeType: formatter.mimeType,
    };
  }

  /**
   * Validate report options for a specific format
   * @param format - The format to validate options for
   * @param options - Options to validate
   * @returns Validation result
   */
  public static validateOptions(
    format: ReportFormat,
    options?: ReportOptions
  ): ReportValidationResult {
    const formatter = this.getFormatter(format);
    if (!formatter) {
      return {
        isValid: false,
        errors: [`Unsupported format: ${format}`],
        warnings: [],
      };
    }

    return formatter.validateOptions(options);
  }

  /**
   * Register a custom formatter
   * @param format - The format type
   * @param formatter - The formatter implementation
   */
  public static registerFormatter(format: ReportFormat, formatter: ReportFormatter): void {
    this.formatters.set(format, formatter);
  }

  /**
   * Unregister a formatter
   * @param format - The format type to unregister
   */
  public static unregisterFormatter(format: ReportFormat): void {
    this.formatters.delete(format);
  }

  // Private helper methods

  /**
   * Get formatter for a specific format
   */
  private static getFormatter(format: ReportFormat): ReportFormatter | undefined {
    return this.formatters.get(format);
  }

  /**
   * Merge default options with provided options
   */
  private static mergeDefaultOptions(
    options?: ReportOptions,
    format?: ReportFormat
  ): ReportOptions {
    const defaults: ReportOptions = {
      includeDetails: true,
      colorOutput: format === ReportFormat.CONSOLE,
      sortBy: 'severity',
      includeMetadata: true,
      includeRecommendations: true,
      maxIssuesPerModule: 10,
      theme: 'light',
      interactive: format === ReportFormat.HTML,
      includeCharts: format === ReportFormat.HTML,
      compact: false,
      timestampInFilename: false,
    };

    return { ...defaults, ...options };
  }

  /**
   * Resolve the final output path with proper extension and timestamp
   */
  private static resolveOutputPath(
    outputPath: string,
    format: ReportFormat,
    options?: ReportOptions
  ): string {
    const formatter = this.getFormatter(format);
    if (!formatter) {
      throw new Error(`Unknown format: ${format}`);
    }

    let finalPath = outputPath;

    // Add extension if not present
    if (!path.extname(finalPath)) {
      finalPath += formatter.extension;
    }

    // Add timestamp if requested
    if (options?.timestampInFilename) {
      const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
      const ext = path.extname(finalPath);
      const base = path.basename(finalPath, ext);
      const dir = path.dirname(finalPath);
      finalPath = path.join(dir, `${base}-${timestamp}${ext}`);
    }

    return finalPath;
  }

  /**
   * Get default filename for a report
   */
  private static getDefaultFilename(
    report: MonitoringReport,
    format: ReportFormat,
    options?: ReportOptions
  ): string {
    const formatter = this.getFormatter(format);
    if (!formatter) {
      throw new Error(`Unknown format: ${format}`);
    }

    const sanitizedDiamondName = report.diamond.name.replace(/[^a-zA-Z0-9]/g, '_');
    const sanitizedNetworkName = report.network.name.replace(/[^a-zA-Z0-9]/g, '_');

    let basename = `diamond-monitoring-${sanitizedDiamondName}-${sanitizedNetworkName}`;

    // Add timestamp if requested
    if (options?.timestampInFilename) {
      const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
      basename += `-${timestamp}`;
    }

    return basename + formatter.extension;
  }
}

/**
 * Types and interfaces for the report generation system
 */

import { MonitoringReport } from '../core/types';

/**
 * Supported report formats
 */
export enum ReportFormat {
  CONSOLE = 'console',
  JSON = 'json',
  HTML = 'html',
  MARKDOWN = 'markdown',
  CSV = 'csv',
}

/**
 * Report generation options
 */
export interface ReportOptions {
  /** Include detailed information in the report */
  includeDetails?: boolean;

  /** Enable color output (for console format) */
  colorOutput?: boolean;

  /** Sort issues by specified criteria */
  sortBy?: 'severity' | 'module' | 'category' | 'timestamp';

  /** Filter issues by severity level */
  severityFilter?: string[];

  /** Filter issues by module */
  moduleFilter?: string[];

  /** Include metadata in the report */
  includeMetadata?: boolean;

  /** Custom title for the report */
  title?: string;

  /** Custom description for the report */
  description?: string;

  /** Include recommendations section */
  includeRecommendations?: boolean;

  /** Maximum number of issues to display per module */
  maxIssuesPerModule?: number;

  /** Theme for HTML reports */
  theme?: 'light' | 'dark' | 'auto';

  /** Include interactive features (for HTML) */
  interactive?: boolean;

  /** Custom CSS for HTML reports */
  customCss?: string;

  /** Include charts and visualizations */
  includeCharts?: boolean;

  /** Compact mode for smaller output */
  compact?: boolean;

  /** Include timestamp in filename */
  timestampInFilename?: boolean;
}

/**
 * Base interface for report formatters
 */
export interface ReportFormatter {
  /** Unique identifier for the formatter */
  readonly id: string;

  /** Human-readable name of the formatter */
  readonly name: string;

  /** File extension for this format */
  readonly extension: string;

  /** MIME type for this format */
  readonly mimeType: string;

  /**
   * Format the monitoring report
   * @param report - The monitoring report to format
   * @param options - Formatting options
   * @returns The formatted report content
   */
  format(report: MonitoringReport, options?: ReportOptions): Promise<string>;

  /**
   * Validate options for this formatter
   * @param options - Options to validate
   * @returns Validation result
   */
  validateOptions(options?: ReportOptions): ReportValidationResult;
}

/**
 * Validation result for report options
 */
export interface ReportValidationResult {
  /** Whether the options are valid */
  isValid: boolean;

  /** Validation errors */
  errors: string[];

  /** Validation warnings */
  warnings: string[];
}

/**
 * Report generation result
 */
export interface ReportGenerationResult {
  /** Whether the generation was successful */
  success: boolean;

  /** Generated content (if successful) */
  content?: string;

  /** Output file path (if saved) */
  filePath?: string;

  /** Generation time in milliseconds */
  duration: number;

  /** File size in bytes (if saved) */
  fileSize?: number;

  /** Error message (if failed) */
  error?: string;

  /** Warnings during generation */
  warnings: string[];
}

/**
 * Chart data for visualizations
 */
export interface ChartData {
  /** Chart type */
  type: 'pie' | 'bar' | 'line' | 'doughnut';

  /** Chart title */
  title: string;

  /** Data labels */
  labels: string[];

  /** Data values */
  data: number[];

  /** Background colors */
  backgroundColor?: string[];

  /** Border colors */
  borderColor?: string[];
}

/**
 * Theme configuration for reports
 */
export interface ReportTheme {
  /** Primary color */
  primaryColor: string;

  /** Secondary color */
  secondaryColor: string;

  /** Success color */
  successColor: string;

  /** Warning color */
  warningColor: string;

  /** Error color */
  errorColor: string;

  /** Critical color */
  criticalColor: string;

  /** Background color */
  backgroundColor: string;

  /** Text color */
  textColor: string;

  /** Border color */
  borderColor: string;

  /** Font family */
  fontFamily: string;

  /** Font size */
  fontSize: string;
}

