/**
 * Console Report Formatter
 *
 * Generates rich, colorful console output with progress indicators,
 * hierarchical display, and professional styling.
 */

import * as chalk from 'chalk';
import { MonitoringReport, MonitoringStatus, SeverityLevel } from '../../core/types';
import { ReportFormatter, ReportOptions, ReportValidationResult } from '../types';

/**
 * Console formatter with rich styling and interactivity
 */
export class ConsoleFormatter implements ReportFormatter {
  public readonly id = 'console';
  public readonly name = 'Console Output';
  public readonly extension = '.txt';
  public readonly mimeType = 'text/plain';

  /**
   * Format the monitoring report for console output
   */
  public async format(report: MonitoringReport, options?: ReportOptions): Promise<string> {
    const output: string[] = [];
    const useColor = options?.colorOutput !== false;
    const c = useColor ? chalk : this.createNoColorChalk();

    // Header section
    output.push(this.generateHeader(report, c, options));

    // Summary section
    output.push(this.generateSummary(report, c, options));

    // Statistics section
    output.push(this.generateStatistics(report, c, options));

    // Module results section
    if (options?.includeDetails !== false) {
      output.push(this.generateModuleResults(report, c, options));
    }

    // Critical issues section
    if (options?.includeDetails) {
      output.push(this.generateCriticalIssues(report, c, options));
    }

    // Recommendations section
    if (options?.includeRecommendations && report.recommendations?.length) {
      output.push(this.generateRecommendations(report, c, options));
    }

    // Footer section
    output.push(this.generateFooter(report, c, options));

    return output.filter(section => section.trim()).join('\n\n');
  }

  /**
   * Validate options for console formatting
   */
  public validateOptions(options?: ReportOptions): ReportValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    if (options?.maxIssuesPerModule !== undefined && options.maxIssuesPerModule < 1) {
      errors.push('maxIssuesPerModule must be at least 1');
    }

    if (
      options?.sortBy &&
      !['severity', 'module', 'category', 'timestamp'].includes(options.sortBy)
    ) {
      errors.push('sortBy must be one of: severity, module, category, timestamp');
    }

    return {
      isValid: errors.length === 0,
      errors,
      warnings,
    };
  }

  // Private helper methods

  /**
   * Generate header section
   */
  private generateHeader(report: MonitoringReport, c: any, options?: ReportOptions): string {
    const lines: string[] = [];

    lines.push(c.blue(''.repeat(80)));
    lines.push(c.blue.bold('              DIAMOND VERIFICATION REPORT              '));
    lines.push(c.blue(''.repeat(80)));

    return lines.join('\n');
  }

  /**
   * Generate summary section
   */
  private generateSummary(report: MonitoringReport, c: any, options?: ReportOptions): string {
    const lines: string[] = [];

    lines.push(c.cyan(' SUMMARY'));
    lines.push(c.cyan(''.repeat(40)));

    lines.push(`Diamond: ${c.white.bold(report.diamond.name)}`);
    lines.push(`Address: ${c.white(report.diamond.address)}`);
    lines.push(`Network: ${c.white(report.network.name)} (Chain ID: ${report.network.chainId})`);

    if (options?.includeMetadata) {
      lines.push(`Timestamp: ${c.white(report.timestamp.toISOString())}`);
      lines.push(`Duration: ${c.white((report.duration / 1000).toFixed(2))}s`);
    }

    // Status with proper styling
    const statusColor = this.getStatusColor(report.summary.status, c);
    const statusIcon = this.getStatusIcon(report.summary.status);
    lines.push('');
    lines.push(`Status: ${statusColor(`${statusIcon} ${report.summary.status}`)}`);

    return lines.join('\n');
  }

  /**
   * Generate statistics section
   */
  private generateStatistics(report: MonitoringReport, c: any, options?: ReportOptions): string {
    const lines: string[] = [];

    lines.push(c.cyan(' STATISTICS'));
    lines.push(c.cyan(''.repeat(40)));

    lines.push(`Total Checks: ${c.white.bold(report.summary.totalChecks)}`);

    // Visual progress bar for checks
    const passRate =
      report.summary.totalChecks > 0
        ? (report.summary.passed / report.summary.totalChecks) * 100
        : 0;

    if (!options?.compact) {
      lines.push(this.generateProgressBar(passRate, c));
    }

    lines.push(`${c.green(' Passed:')} ${report.summary.passed}`);

    if (report.summary.failed > 0) {
      lines.push(`${c.red(' Failed:')} ${report.summary.failed}`);
    }

    if (report.summary.warnings > 0) {
      lines.push(`${c.yellow('  Warnings:')} ${report.summary.warnings}`);
    }

    if (report.summary.skipped > 0) {
      lines.push(`${c.gray('  Skipped:')} ${report.summary.skipped}`);
    }

    return lines.join('\n');
  }

  /**
   * Generate module results section
   */
  private generateModuleResults(report: MonitoringReport, c: any, options?: ReportOptions): string {
    const lines: string[] = [];

    lines.push(c.cyan(' MODULE RESULTS'));
    lines.push(c.cyan(''.repeat(40)));

    const maxIssues = options?.maxIssuesPerModule || 10;

    for (const moduleResult of report.modules) {
      const moduleStatusColor = this.getStatusColor(moduleResult.status, c);
      const moduleIcon = this.getStatusIcon(moduleResult.status);

      lines.push('');
      lines.push(`${moduleStatusColor(`${moduleIcon} ${moduleResult.moduleName}`)}`);

      if (!options?.compact) {
        lines.push(`   Duration: ${c.gray((moduleResult.duration / 1000).toFixed(2))}s`);
        lines.push(`   Issues: ${this.getIssueCountText(moduleResult.result.issues.length, c)}`);
      }

      if (moduleResult.result.issues.length > 0) {
        const sortedIssues = this.sortIssues(moduleResult.result.issues, options?.sortBy);
        const issuesToShow = sortedIssues.slice(0, maxIssues);

        for (const issue of issuesToShow) {
          const issueColor = this.getSeverityColor(issue.severity, c);
          const issueIcon = this.getSeverityIcon(issue.severity);
          lines.push(`     ${issueColor(`${issueIcon} ${issue.title}`)}`);

          if (options?.includeDetails && !options?.compact) {
            lines.push(`       ${c.gray(this.truncateText(issue.description, 80))}`);
          }
        }

        if (moduleResult.result.issues.length > maxIssues) {
          const remaining = moduleResult.result.issues.length - maxIssues;
          lines.push(
            `     ${c.gray(`... and ${remaining} more issue${remaining === 1 ? '' : 's'}`)}`
          );
        }
      }

      // Show metadata if available and detailed
      if (options?.includeMetadata && moduleResult.result.metadata && !options?.compact) {
        const metadata = moduleResult.result.metadata;
        Object.entries(metadata).forEach(([key, value]) => {
          if (typeof value === 'number' || typeof value === 'string') {
            lines.push(`   ${c.dim(`${key}: ${value}`)}`);
          }
        });
      }
    }

    return lines.join('\n');
  }

  /**
   * Generate critical issues section
   */
  private generateCriticalIssues(
    report: MonitoringReport,
    c: any,
    options?: ReportOptions
  ): string {
    const allIssues = report.modules.flatMap(m => m.result.issues);
    const criticalIssues = allIssues.filter(i => i.severity === SeverityLevel.CRITICAL);
    const errorIssues = allIssues.filter(i => i.severity === SeverityLevel.ERROR);

    if (criticalIssues.length === 0 && errorIssues.length === 0) {
      return '';
    }

    const lines: string[] = [];
    lines.push(c.red(' CRITICAL & ERROR ISSUES'));
    lines.push(c.red(''.repeat(40)));

    const highPriorityIssues = [...criticalIssues, ...errorIssues];

    highPriorityIssues.forEach((issue, index) => {
      const issueColor = this.getSeverityColor(issue.severity, c);
      const issueIcon = this.getSeverityIcon(issue.severity);

      lines.push('');
      lines.push(`${index + 1}. ${issueColor(`${issueIcon} ${issue.title}`)}`);
      lines.push(`   Category: ${c.dim(issue.category)}`);
      lines.push(`   ${this.wrapText(issue.description, 77, '   ')}`);

      if (issue.recommendation) {
        lines.push(
          `   ${c.blue(' Recommendation:')} ${this.wrapText(issue.recommendation, 60, '      ')}`
        );
      }

      if (issue.metadata && options?.includeMetadata) {
        lines.push(
          `   ${c.dim('Metadata: ' + JSON.stringify(issue.metadata, null, 2).replace(/\n/g, '\n   '))}`
        );
      }
    });

    return lines.join('\n');
  }

  /**
   * Generate recommendations section
   */
  private generateRecommendations(
    report: MonitoringReport,
    c: any,
    options?: ReportOptions
  ): string {
    if (!report.recommendations?.length) return '';

    const lines: string[] = [];
    lines.push(c.blue(' RECOMMENDATIONS'));
    lines.push(c.blue(''.repeat(40)));

    report.recommendations.forEach((recommendation, index) => {
      lines.push(`${index + 1}. ${this.wrapText(recommendation, 75, '   ')}`);
    });

    return lines.join('\n');
  }

  /**
   * Generate footer section
   */
  private generateFooter(report: MonitoringReport, c: any, options?: ReportOptions): string {
    const lines: string[] = [];

    lines.push(c.blue(''.repeat(80)));

    // Success message based on status
    switch (report.summary.status) {
      case MonitoringStatus.PASS:
        lines.push(c.green(' Diamond monitoring completed successfully'));
        break;
      case MonitoringStatus.WARNING:
        lines.push(c.yellow('  Diamond monitoring completed with warnings'));
        break;
      case MonitoringStatus.FAIL:
        lines.push(c.red(' Diamond monitoring found critical issues'));
        break;
      default:
        lines.push(c.gray('  Diamond monitoring completed'));
    }

    if (!options?.compact) {
      const moduleCount = report.modules.length;
      const passedModules = report.modules.filter(m => m.status === MonitoringStatus.PASS).length;
      lines.push('');
      lines.push(c.gray(`Monitoring completed - ${passedModules}/${moduleCount} modules passed`));
    }

    return lines.join('\n');
  }

  // Utility methods

  /**
   * Generate a progress bar
   */
  private generateProgressBar(percentage: number, c: any, width: number = 40): string {
    const filled = Math.round((percentage / 100) * width);
    const empty = width - filled;
    const bar = ''.repeat(filled) + ''.repeat(empty);

    const color = percentage >= 80 ? c.green : percentage >= 60 ? c.yellow : c.red;
    return `Progress: [${color(bar)}] ${percentage.toFixed(1)}%`;
  }

  /**
   * Get color for status
   */
  private getStatusColor(status: MonitoringStatus, c: any): any {
    switch (status) {
      case MonitoringStatus.PASS:
        return c.green.bold;
      case MonitoringStatus.WARNING:
        return c.yellow.bold;
      case MonitoringStatus.FAIL:
        return c.red.bold;
      case MonitoringStatus.SKIPPED:
        return c.gray.bold;
      default:
        return c.white.bold;
    }
  }

  /**
   * Get icon for status
   */
  private getStatusIcon(status: MonitoringStatus): string {
    switch (status) {
      case MonitoringStatus.PASS:
        return '';
      case MonitoringStatus.WARNING:
        return '';
      case MonitoringStatus.FAIL:
        return '';
      case MonitoringStatus.SKIPPED:
        return '';
      default:
        return '';
    }
  }

  /**
   * Get color for severity
   */
  private getSeverityColor(severity: SeverityLevel, c: any): any {
    switch (severity) {
      case SeverityLevel.CRITICAL:
        return c.red.bold;
      case SeverityLevel.ERROR:
        return c.red;
      case SeverityLevel.WARNING:
        return c.yellow;
      case SeverityLevel.INFO:
        return c.blue;
      default:
        return c.gray;
    }
  }

  /**
   * Get icon for severity
   */
  private getSeverityIcon(severity: SeverityLevel): string {
    switch (severity) {
      case SeverityLevel.CRITICAL:
        return '';
      case SeverityLevel.ERROR:
        return '';
      case SeverityLevel.WARNING:
        return '';
      case SeverityLevel.INFO:
        return '';
      default:
        return '';
    }
  }

  /**
   * Get issue count text with appropriate color
   */
  private getIssueCountText(count: number, c: any): string {
    if (count === 0) return c.green('0');
    if (count <= 3) return c.yellow(count.toString());
    return c.red(count.toString());
  }

  /**
   * Sort issues by specified criteria
   */
  private sortIssues(issues: any[], sortBy?: string): any[] {
    const sorted = [...issues];

    switch (sortBy) {
      case 'severity':
        const severityOrder: Record<string, number> = {
          [SeverityLevel.CRITICAL]: 0,
          [SeverityLevel.ERROR]: 1,
          [SeverityLevel.WARNING]: 2,
          [SeverityLevel.INFO]: 3,
        };
        sorted.sort(
          (a, b) => (severityOrder[a.severity] || 999) - (severityOrder[b.severity] || 999)
        );
        break;
      case 'category':
        sorted.sort((a, b) => a.category.localeCompare(b.category));
        break;
      case 'module':
        // Keep original order since they're already grouped by module
        break;
    }

    return sorted;
  }

  /**
   * Truncate text to specified length
   */
  private truncateText(text: string, maxLength: number): string {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength - 3) + '...';
  }

  /**
   * Wrap text to specified width with indentation
   */
  private wrapText(text: string, width: number, indent: string = ''): string {
    const words = text.split(' ');
    const lines: string[] = [];
    let currentLine = '';

    for (const word of words) {
      if ((currentLine + word).length > width) {
        if (currentLine) {
          lines.push(indent + currentLine.trim());
          currentLine = '';
        }
      }
      currentLine += word + ' ';
    }

    if (currentLine.trim()) {
      lines.push(indent + currentLine.trim());
    }

    return lines.join('\n');
  }

  /**
   * Create no-color chalk replacement
   */
  private createNoColorChalk(): any {
    const identity = (text: string) => text;
    const boldIdentity = Object.assign(identity, { bold: identity });

    return new Proxy(
      {},
      {
        get: () =>
          new Proxy(boldIdentity, {
            get: () => identity,
            apply: (target, thisArg, args) => args[0] || '',
          }),
      }
    );
  }
}

/**
 * JSON Report Formatter
 *
 * Generates structured JSON reports suitable for CI/CD integration,
 * machine processing, and API consumption.
 */

import { MonitoringReport } from '../../core/types';
import { ReportFormatter, ReportOptions, ReportValidationResult } from '../types';

/**
 * JSON formatter for machine-readable reports
 */
export class JSONFormatter implements ReportFormatter {
  public readonly id = 'json';
  public readonly name = 'JSON Report';
  public readonly extension = '.json';
  public readonly mimeType = 'application/json';

  /**
   * Format the monitoring report as JSON
   */
  public async format(report: MonitoringReport, options?: ReportOptions): Promise<string> {
    const jsonReport = this.createJSONReport(report, options);

    // Pretty print or compact based on options
    const indentation = options?.compact ? 0 : 2;

    return JSON.stringify(jsonReport, this.createReplacer(options), indentation);
  }

  /**
   * Validate options for JSON formatting
   */
  public validateOptions(options?: ReportOptions): ReportValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    // JSON formatter accepts most options, but warn about irrelevant ones
    if (options?.colorOutput) {
      warnings.push('colorOutput option is ignored for JSON format');
    }

    if (options?.interactive) {
      warnings.push('interactive option is ignored for JSON format');
    }

    if (options?.theme) {
      warnings.push('theme option is ignored for JSON format');
    }

    return {
      isValid: true,
      errors,
      warnings,
    };
  }

  // Private helper methods

  /**
   * Create the complete JSON report structure
   */
  private createJSONReport(report: MonitoringReport, options?: ReportOptions): any {
    const jsonReport: any = {
      // Metadata
      reportMetadata: {
        generatedAt: new Date().toISOString(),
        formatVersion: '1.0.0',
        generatorVersion: '1.0.0',
        options: this.sanitizeOptions(options),
      },

      // Core report data
      summary: {
        status: report.summary.status,
        totalChecks: report.summary.totalChecks,
        passed: report.summary.passed,
        failed: report.summary.failed,
        warnings: report.summary.warnings,
        skipped: report.summary.skipped,
        successRate:
          report.summary.totalChecks > 0
            ? ((report.summary.passed / report.summary.totalChecks) * 100).toFixed(2) + '%'
            : '0%',
      },

      // Diamond information
      diamond: {
        name: report.diamond.name,
        address: report.diamond.address,
        configPath: report.diamond.configPath,
        deploymentBlock: report.diamond.deploymentBlock,
      },

      // Network information
      network: {
        name: report.network.name,
        chainId: report.network.chainId,
        rpcUrl: report.network.rpcUrl,
        blockExplorerUrl: report.network.blockExplorerUrl,
      },

      // Execution metadata
      execution: {
        timestamp: report.timestamp.toISOString(),
        duration: report.duration,
        durationHuman: this.formatDuration(report.duration),
      },

      // Module results
      modules: this.formatModules(report.modules, options),

      // Configuration
      config: options?.includeMetadata ? this.sanitizeConfig(report.config) : undefined,
    };

    // Add recommendations if present and requested
    if (options?.includeRecommendations && report.recommendations?.length) {
      jsonReport.recommendations = report.recommendations;
    }

    // Add aggregated statistics
    if (options?.includeMetadata) {
      jsonReport.statistics = this.generateStatistics(report);
    }

    // Add issue analysis
    jsonReport.issueAnalysis = this.analyzeIssues(report, options);

    return jsonReport;
  }

  /**
   * Format module results for JSON
   */
  private formatModules(modules: any[], options?: ReportOptions): any[] {
    return modules.map(moduleResult => {
      const formattedModule: any = {
        moduleId: moduleResult.moduleId,
        moduleName: moduleResult.moduleName,
        status: moduleResult.status,
        execution: {
          startTime: moduleResult.startTime.toISOString(),
          endTime: moduleResult.endTime.toISOString(),
          duration: moduleResult.duration,
          durationHuman: this.formatDuration(moduleResult.duration),
        },
        result: {
          status: moduleResult.result.status,
          executionTime: moduleResult.result.executionTime,
          issueCount: moduleResult.result.issues.length,
          issues: this.formatIssues(moduleResult.result.issues, options),
        },
      };

      // Add metadata if present and requested
      if (options?.includeMetadata && moduleResult.result.metadata) {
        formattedModule.result.metadata = moduleResult.result.metadata;
      }

      // Add error information if present
      if (moduleResult.error) {
        formattedModule.error = {
          message: moduleResult.error.message,
          name: moduleResult.error.name,
          stack: options?.includeMetadata ? moduleResult.error.stack : undefined,
        };
      }

      return formattedModule;
    });
  }

  /**
   * Format issues for JSON
   */
  private formatIssues(issues: any[], options?: ReportOptions): any[] {
    let processedIssues = [...issues];

    // Apply sorting if specified
    if (options?.sortBy) {
      processedIssues = this.sortIssues(processedIssues, options.sortBy);
    }

    // Apply filtering if specified
    if (options?.severityFilter?.length) {
      processedIssues = processedIssues.filter(issue =>
        options.severityFilter!.includes(issue.severity)
      );
    }

    // Apply limit if specified
    const maxIssues = options?.maxIssuesPerModule;
    if (maxIssues && maxIssues > 0) {
      processedIssues = processedIssues.slice(0, maxIssues);
    }

    return processedIssues.map(issue => ({
      id: issue.id,
      title: issue.title,
      description: issue.description,
      severity: issue.severity,
      category: issue.category,
      recommendation: issue.recommendation,
      metadata: options?.includeMetadata ? issue.metadata : undefined,
    }));
  }

  /**
   * Generate aggregated statistics
   */
  private generateStatistics(report: MonitoringReport): any {
    const allIssues = report.modules.flatMap(m => m.result.issues);

    // Issue count by severity
    const severityCounts = {
      critical: allIssues.filter(i => i.severity === 'critical').length,
      error: allIssues.filter(i => i.severity === 'error').length,
      warning: allIssues.filter(i => i.severity === 'warning').length,
      info: allIssues.filter(i => i.severity === 'info').length,
    };

    // Issue count by category
    const categoryMap = new Map<string, number>();
    allIssues.forEach(issue => {
      categoryMap.set(issue.category, (categoryMap.get(issue.category) || 0) + 1);
    });

    // Module performance
    const modulePerformance = report.modules.map(m => ({
      name: m.moduleName,
      duration: m.duration,
      issueCount: m.result.issues.length,
      status: m.status,
    }));

    return {
      totalIssues: allIssues.length,
      severityDistribution: severityCounts,
      categoryDistribution: Object.fromEntries(categoryMap),
      modulePerformance,
      averageModuleDuration:
        report.modules.length > 0
          ? Math.round(
              report.modules.reduce((sum, m) => sum + m.duration, 0) / report.modules.length
            )
          : 0,
    };
  }

  /**
   * Analyze issues for insights
   */
  private analyzeIssues(report: MonitoringReport, options?: ReportOptions): any {
    const allIssues = report.modules.flatMap(m => m.result.issues);

    // Top issues by severity
    const criticalIssues = allIssues.filter(i => i.severity === 'critical');
    const errorIssues = allIssues.filter(i => i.severity === 'error');

    // Most problematic modules
    const moduleIssueMap = new Map<string, number>();
    report.modules.forEach(m => {
      moduleIssueMap.set(m.moduleName, m.result.issues.length);
    });

    const mostProblematicModules = Array.from(moduleIssueMap.entries())
      .sort(([, a], [, b]) => b - a)
      .slice(0, 3)
      .map(([name, count]) => ({ name, issueCount: count }));

    // Common issue categories
    const categoryMap = new Map<string, number>();
    allIssues.forEach(issue => {
      categoryMap.set(issue.category, (categoryMap.get(issue.category) || 0) + 1);
    });

    const topCategories = Array.from(categoryMap.entries())
      .sort(([, a], [, b]) => b - a)
      .slice(0, 5)
      .map(([category, count]) => ({ category, count }));

    return {
      highPriorityIssues: {
        critical: criticalIssues.length,
        error: errorIssues.length,
        criticalTitles: criticalIssues.slice(0, 5).map(i => i.title),
        errorTitles: errorIssues.slice(0, 5).map(i => i.title),
      },
      mostProblematicModules,
      topIssueCategories: topCategories,
      hasBlockingIssues: criticalIssues.length > 0 || errorIssues.length > 0,
      recommendsAction: criticalIssues.length > 0,
    };
  }

  /**
   * Sort issues by specified criteria
   */
  private sortIssues(issues: any[], sortBy: string): any[] {
    const sorted = [...issues];

    switch (sortBy) {
      case 'severity':
        const severityOrder: Record<string, number> = {
          critical: 0,
          error: 1,
          warning: 2,
          info: 3,
        };
        sorted.sort(
          (a, b) => (severityOrder[a.severity] || 999) - (severityOrder[b.severity] || 999)
        );
        break;
      case 'category':
        sorted.sort((a, b) => a.category.localeCompare(b.category));
        break;
      case 'timestamp':
        // If issues have timestamps, sort by them
        sorted.sort((a, b) => {
          const timestampA = a.metadata?.timestamp || 0;
          const timestampB = b.metadata?.timestamp || 0;
          return timestampB - timestampA; // Most recent first
        });
        break;
    }

    return sorted;
  }

  /**
   * Format duration in human-readable format
   */
  private formatDuration(ms: number): string {
    if (ms < 1000) {
      return `${ms}ms`;
    } else if (ms < 60000) {
      return `${(ms / 1000).toFixed(2)}s`;
    } else {
      const minutes = Math.floor(ms / 60000);
      const seconds = ((ms % 60000) / 1000).toFixed(0);
      return `${minutes}m ${seconds}s`;
    }
  }

  /**
   * Sanitize options for JSON output
   */
  private sanitizeOptions(options?: ReportOptions): any {
    if (!options) return {};

    return {
      includeDetails: options.includeDetails,
      sortBy: options.sortBy,
      severityFilter: options.severityFilter,
      moduleFilter: options.moduleFilter,
      includeMetadata: options.includeMetadata,
      includeRecommendations: options.includeRecommendations,
      maxIssuesPerModule: options.maxIssuesPerModule,
      compact: options.compact,
    };
  }

  /**
   * Sanitize config for JSON output
   */
  private sanitizeConfig(config: any): any {
    // Remove sensitive information and functions from config
    const sanitized = JSON.parse(
      JSON.stringify(config, (key, value) => {
        // Remove functions
        if (typeof value === 'function') return '[Function]';

        // Remove potentially sensitive keys
        if (
          key.toLowerCase().includes('key') ||
          key.toLowerCase().includes('secret') ||
          key.toLowerCase().includes('password')
        ) {
          return '[REDACTED]';
        }

        return value;
      })
    );

    return sanitized;
  }

  /**
   * Create JSON replacer function
   */
  private createReplacer(options?: ReportOptions): ((key: string, value: any) => any) | undefined {
    if (!options?.compact) return undefined;

    return (key: string, value: any) => {
      // Remove null/undefined values in compact mode
      if (value === null || value === undefined) return undefined;

      // Remove empty arrays and objects in compact mode
      if (Array.isArray(value) && value.length === 0) return undefined;
      if (typeof value === 'object' && Object.keys(value).length === 0) return undefined;

      return value;
    };
  }
}

/**
 * HTML Report Formatter
 *
 * Generates interactive HTML reports with professional styling,
 * charts, filtering capabilities, and responsive design.
 */

import { MonitoringReport, MonitoringStatus, SeverityLevel } from '../../core/types';
import {
  ReportFormatter,
  ReportOptions,
  ReportValidationResult,
  THEMES,
  ChartData,
} from '../types';

/**
 * HTML formatter with interactive features
 */
export class HTMLFormatter implements ReportFormatter {
  public readonly id = 'html';
  public readonly name = 'HTML Report';
  public readonly extension = '.html';
  public readonly mimeType = 'text/html';

  /**
   * Format the monitoring report as interactive HTML
   */
  public async format(report: MonitoringReport, options?: ReportOptions): Promise<string> {
    const theme = THEMES[options?.theme || 'light'];
    const chartData = options?.includeCharts ? this.generateChartData(report) : undefined;

    return this.generateHTMLDocument(report, options, theme, chartData);
  }

  /**
   * Validate options for HTML formatting
   */
  public validateOptions(options?: ReportOptions): ReportValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    if (options?.theme && !THEMES[options.theme] && options.theme !== 'auto') {
      errors.push(
        `Invalid theme: ${options.theme}. Available themes: ${Object.keys(THEMES).join(', ')}, auto`
      );
    }

    if (options?.colorOutput === false) {
      warnings.push('colorOutput=false is ignored for HTML format (always uses colors)');
    }

    return {
      isValid: errors.length === 0,
      errors,
      warnings,
    };
  }

  // Private helper methods

  /**
   * Generate the complete HTML document
   */
  private generateHTMLDocument(
    report: MonitoringReport,
    options?: ReportOptions,
    theme?: any,
    chartData?: ChartData[]
  ): string {
    const title = options?.title || `Diamond Monitoring Report - ${report.diamond.name}`;
    const description =
      options?.description ||
      `Monitoring report for ${report.diamond.name} on ${report.network.name}`;

    return `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <meta name="description" content="${description}">
  <title>${title}</title>
  ${this.generateCSS(theme, options)}
  ${options?.includeCharts ? this.generateChartJS() : ''}
</head>
<body>
  <div class="container">
    ${this.generateHeader(report, options)}
    ${this.generateSummary(report, options)}
    ${options?.includeCharts && chartData ? this.generateCharts(chartData) : ''}
    ${this.generateModuleResults(report, options)}
    ${this.generateDetailedIssues(report, options)}
    ${options?.includeRecommendations ? this.generateRecommendations(report, options) : ''}
    ${this.generateFooter(report, options)}
  </div>

  ${options?.interactive ? this.generateJavaScript(options) : ''}
</body>
</html>`;
  }

