  }

  /**
   * Generate detailed issues section
   */
  private generateDetailedIssues(report: MonitoringReport, options?: ReportOptions): string {
    const allIssues = report.modules.flatMap(m =>
      m.result.issues.map(issue => ({ ...issue, module: m.moduleName }))
    );
    const criticalIssues = allIssues.filter(i => i.severity === SeverityLevel.CRITICAL);
    const errorIssues = allIssues.filter(i => i.severity === SeverityLevel.ERROR);

    if (criticalIssues.length === 0 && errorIssues.length === 0) {
      return '';
    }

    const highPriorityIssues = [...criticalIssues, ...errorIssues];

    return `
    <div class="section">
      <h2 class="section-title">
        <span class="icon"></span>
        High Priority Issues
      </h2>

      <div style="margin-top: 20px;">
        ${highPriorityIssues
          .map(
            (issue, index) => `
          <div class="issue ${issue.severity}" style="margin-bottom: 20px;">
            <div class="issue-title">
              ${index + 1}. ${this.getSeverityIcon(issue.severity)} ${issue.title}
            </div>
            <div style="margin: 10px 0; font-size: 0.9em; color: #666;">
              <strong>Module:</strong> ${issue.module}  <strong>Category:</strong> ${issue.category}
            </div>
            <div class="issue-description">${issue.description}</div>
            ${
              issue.recommendation
                ? `
              <div class="issue-recommendation">
                 <strong>Recommendation:</strong> ${issue.recommendation}
              </div>
            `
                : ''
            }
          </div>
        `
          )
          .join('')}
      </div>
    </div>`;
  }

  /**
   * Generate recommendations section
   */
  private generateRecommendations(report: MonitoringReport, options?: ReportOptions): string {
    if (!report.recommendations?.length) return '';

    return `
    <div class="section">
      <h2 class="section-title">
        <span class="icon"></span>
        Recommendations
      </h2>

      <div style="background: #e3f2fd; padding: 20px; border-radius: 8px; border-left: 4px solid #1976d2;">
        <ol style="margin-left: 20px;">
          ${report.recommendations.map(rec => `<li style="margin-bottom: 10px;">${rec}</li>`).join('')}
        </ol>
      </div>
    </div>`;
  }

  /**
   * Generate footer section
   */
  private generateFooter(report: MonitoringReport, options?: ReportOptions): string {
    return `
    <div style="margin-top: 50px; padding-top: 30px; border-top: 2px solid #e0e0e0; text-align: center; color: #666;">
      <p>Generated by Diamond Monitoring System v1.0.0</p>
      <p>Report generated at ${new Date().toLocaleString()}</p>
      ${
        options?.includeMetadata
          ? `
        <p style="margin-top: 10px; font-size: 0.9em;">
          Execution time: ${(report.duration / 1000).toFixed(2)}s  
          Modules: ${report.modules.length}  
          Total issues: ${report.modules.reduce((sum, m) => sum + m.result.issues.length, 0)}
        </p>
      `
          : ''
      }
    </div>`;
  }

  /**
   * Generate JavaScript for interactivity
   */
  private generateJavaScript(options?: ReportOptions): string {
    return `
    <script>
      // Initialize when DOM is loaded
      document.addEventListener('DOMContentLoaded', function() {
        initializeFilters();
        ${options?.includeCharts ? 'initializeCharts();' : ''}
        addAnimations();
      });

      function initializeFilters() {
        // Populate module filter
        const moduleFilter = document.getElementById('module-filter');
        const modules = [...new Set(Array.from(document.querySelectorAll('[data-module]')).map(el => el.dataset.module))];
        modules.forEach(module => {
          const option = document.createElement('option');
          option.value = module;
          option.textContent = module.replace(/([A-Z])/g, ' $1').trim();
          moduleFilter.appendChild(option);
        });
      }

      function filterIssues() {
        const severityFilter = document.getElementById('severity-filter').value;
        const moduleFilter = document.getElementById('module-filter').value;
        const searchFilter = document.getElementById('search-filter').value.toLowerCase();

        document.querySelectorAll('.module-card').forEach(card => {
          let showCard = true;

          // Module filter
          if (moduleFilter && card.dataset.module !== moduleFilter) {
            showCard = false;
          }

          // Show/hide card
          card.style.display = showCard ? 'block' : 'none';

          // Filter issues within visible cards
          if (showCard) {
            const issues = card.querySelectorAll('.issue');
            let visibleIssues = 0;

            issues.forEach(issue => {
              let showIssue = true;

              // Severity filter
              if (severityFilter && issue.dataset.severity !== severityFilter) {
                showIssue = false;
              }

              // Search filter
              if (searchFilter && !issue.textContent.toLowerCase().includes(searchFilter)) {
                showIssue = false;
              }

              issue.style.display = showIssue ? 'block' : 'none';
              if (showIssue) visibleIssues++;
            });

            // Update issues container message
            const issuesContainer = card.querySelector('.issues-container');
            if (visibleIssues === 0 && issues.length > 0) {
              if (!issuesContainer.querySelector('.no-results')) {
                const noResults = document.createElement('div');
                noResults.className = 'no-results';
                noResults.style.cssText = 'text-align: center; color: #666; padding: 20px;';
                noResults.textContent = 'No issues match the current filters';
                issuesContainer.appendChild(noResults);
              }
            } else {
              const noResults = issuesContainer.querySelector('.no-results');
              if (noResults) noResults.remove();
            }
          }
        });
      }

      function resetFilters() {
        document.getElementById('severity-filter').value = '';
        document.getElementById('module-filter').value = '';
        document.getElementById('search-filter').value = '';
        filterIssues();
      }

      function addAnimations() {
        // Add fade-in animation to cards
        const cards = document.querySelectorAll('.module-card, .stat-card');
        cards.forEach((card, index) => {
          card.style.animationDelay = (index * 0.1) + 's';
          card.classList.add('fade-in');
        });
      }

      ${options?.includeCharts ? this.generateChartInitialization() : ''}
    </script>`;
  }

  /**
   * Generate chart initialization JavaScript
   */
  private generateChartInitialization(): string {
    return `
      function initializeCharts() {
        // Chart configurations will be injected here based on chart data
        // This is a placeholder for the actual chart initialization
      }`;
  }

  /**
   * Generate chart data for visualizations
   */
  private generateChartData(report: MonitoringReport): ChartData[] {
    const charts: ChartData[] = [];
    const allIssues = report.modules.flatMap(m => m.result.issues);

    // Severity distribution chart
    const severityCounts = {
      critical: allIssues.filter(i => i.severity === 'critical').length,
      error: allIssues.filter(i => i.severity === 'error').length,
      warning: allIssues.filter(i => i.severity === 'warning').length,
      info: allIssues.filter(i => i.severity === 'info').length,
    };

    charts.push({
      type: 'doughnut',
      title: 'Issues by Severity',
      labels: ['Critical', 'Error', 'Warning', 'Info'],
      data: [
        severityCounts.critical,
        severityCounts.error,
        severityCounts.warning,
        severityCounts.info,
      ],
      backgroundColor: ['#d32f2f', '#f57c00', '#fbc02d', '#1976d2'],
    });

    // Module performance chart
    charts.push({
      type: 'bar',
      title: 'Module Execution Time',
      labels: report.modules.map(m => m.moduleName.replace(/([A-Z])/g, ' $1').trim()),
      data: report.modules.map(m => m.duration),
      backgroundColor: ['#42a5f5'],
    });

    return charts;
  }

  // Utility methods

  private getStatusIcon(status: MonitoringStatus): string {
    switch (status) {
      case MonitoringStatus.PASS:
        return '';
      case MonitoringStatus.WARNING:
        return '';
      case MonitoringStatus.FAIL:
        return '';
      case MonitoringStatus.SKIPPED:
        return '';
      default:
        return '';
    }
  }

  private getSeverityIcon(severity: SeverityLevel): string {
    switch (severity) {
      case SeverityLevel.CRITICAL:
        return '';
      case SeverityLevel.ERROR:
        return '';
      case SeverityLevel.WARNING:
        return '';
      case SeverityLevel.INFO:
        return '';
      default:
        return '';
    }
  }
}

/**
 * Markdown Report Formatter
 *
 * Generates GitHub-compatible Markdown reports with proper formatting,
 * tables, code blocks, and documentation-friendly structure.
 */

import { MonitoringReport, MonitoringStatus, SeverityLevel } from '../../core/types';
import { ReportFormatter, ReportOptions, ReportValidationResult } from '../types';

/**
 * Markdown formatter for documentation-friendly reports
 */
export class MarkdownFormatter implements ReportFormatter {
  public readonly id = 'markdown';
  public readonly name = 'Markdown Report';
  public readonly extension = '.md';
  public readonly mimeType = 'text/markdown';

  /**
   * Format the monitoring report as Markdown
   */
  public async format(report: MonitoringReport, options?: ReportOptions): Promise<string> {
    const sections: string[] = [];

    // Title and metadata
    sections.push(this.generateTitle(report, options));
    sections.push(this.generateTOC(report, options));
    sections.push(this.generateSummary(report, options));
    sections.push(this.generateOverview(report, options));

    // Module results
    sections.push(this.generateModuleResults(report, options));

    // Detailed issues
    if (options?.includeDetails) {
      sections.push(this.generateDetailedIssues(report, options));
    }

    // Recommendations
    if (options?.includeRecommendations && report.recommendations?.length) {
      sections.push(this.generateRecommendations(report, options));
    }

    // Appendix
    if (options?.includeMetadata) {
      sections.push(this.generateAppendix(report, options));
    }

    return sections.filter(section => section.trim()).join('\n\n');
  }

  /**
   * Validate options for Markdown formatting
   */
  public validateOptions(options?: ReportOptions): ReportValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    // Warn about options that don't apply to Markdown
    const irrelevantOptions = ['colorOutput', 'interactive', 'theme', 'includeCharts', 'customCss'];
    irrelevantOptions.forEach(option => {
      if (options && (options as any)[option]) {
        warnings.push(`${option} option is ignored for Markdown format`);
      }
    });

    return {
      isValid: true,
      errors,
      warnings,
    };
  }

  // Private helper methods

  /**
   * Generate title and header
   */
  private generateTitle(report: MonitoringReport, options?: ReportOptions): string {
    const title = options?.title || `Diamond Monitoring Report`;
    const statusBadge = this.getStatusBadge(report.summary.status);
    const timestamp = options?.includeMetadata ? ` - ${report.timestamp.toLocaleDateString()}` : '';

    return `# ${title}${timestamp}

${statusBadge}

**Diamond:** ${report.diamond.name}  
**Address:** \`${report.diamond.address}\`  
**Network:** ${report.network.name} (Chain ID: ${report.network.chainId})  
${options?.includeMetadata ? `**Generated:** ${new Date().toISOString()}  \n**Duration:** ${(report.duration / 1000).toFixed(2)}s` : ''}

---`;
  }

  /**
   * Generate table of contents
   */
  private generateTOC(report: MonitoringReport, options?: ReportOptions): string {
    const sections: string[] = [
      '- [Summary](#summary)',
      '- [Overview](#overview)',
      '- [Module Results](#module-results)',
    ];

    if (options?.includeDetails) {
      sections.push('- [Detailed Issues](#detailed-issues)');
    }

    if (options?.includeRecommendations && report.recommendations?.length) {
      sections.push('- [Recommendations](#recommendations)');
    }

    if (options?.includeMetadata) {
      sections.push('- [Technical Details](#technical-details)');
    }

    return `## Table of Contents

${sections.join('\n')}

---`;
  }

  /**
   * Generate summary section
   */
  private generateSummary(report: MonitoringReport, options?: ReportOptions): string {
    const passRate =
      report.summary.totalChecks > 0
        ? ((report.summary.passed / report.summary.totalChecks) * 100).toFixed(1)
        : '0';

    return `## Summary

| Metric | Value |
|--------|-------|
| **Overall Status** | ${this.getStatusBadge(report.summary.status)} |
| **Total Checks** | ${report.summary.totalChecks} |
| **Passed** | ${report.summary.passed}  |
| **Failed** | ${report.summary.failed}  |
| **Warnings** | ${report.summary.warnings}  |
| **Skipped** | ${report.summary.skipped}  |
| **Pass Rate** | ${passRate}% |

${this.generateProgressBar(parseFloat(passRate))}`;
  }

  /**
   * Generate overview section
   */
  private generateOverview(report: MonitoringReport, options?: ReportOptions): string {
    const allIssues = report.modules.flatMap(m => m.result.issues);
    const criticalIssues = allIssues.filter(i => i.severity === SeverityLevel.CRITICAL).length;
    const errorIssues = allIssues.filter(i => i.severity === SeverityLevel.ERROR).length;
    const warningIssues = allIssues.filter(i => i.severity === SeverityLevel.WARNING).length;
    const infoIssues = allIssues.filter(i => i.severity === SeverityLevel.INFO).length;

    let overview = `## Overview

This report provides a comprehensive analysis of the ${report.diamond.name} diamond contract deployed on ${report.network.name}.

### Key Findings

`;

    if (criticalIssues > 0) {
      overview += `-  **${criticalIssues} Critical Issue${criticalIssues === 1 ? '' : 's'}** requiring immediate attention\n`;
    }

    if (errorIssues > 0) {
      overview += `-  **${errorIssues} Error${errorIssues === 1 ? '' : 's'}** that should be addressed\n`;
    }

    if (warningIssues > 0) {
      overview += `-  **${warningIssues} Warning${warningIssues === 1 ? '' : 's'}** for consideration\n`;
    }

    if (infoIssues > 0) {
      overview += `-  **${infoIssues} Informational item${infoIssues === 1 ? '' : 's'}**\n`;
    }

    if (criticalIssues === 0 && errorIssues === 0) {
      overview += `-  **No critical or error issues detected**\n`;
    }

    // Module performance summary
    const moduleTable = this.generateModulePerformanceTable(report);
    overview += `\n### Module Performance\n\n${moduleTable}`;

    return overview;
  }

  /**
   * Generate module results section
   */
  private generateModuleResults(report: MonitoringReport, options?: ReportOptions): string {
    const maxIssues = options?.maxIssuesPerModule || 10;
    let content = `## Module Results

Each module performs specific monitoring tasks. Below are the detailed results:

`;

    for (const moduleResult of report.modules) {
      const statusIcon = this.getStatusIcon(moduleResult.status);
      const duration = (moduleResult.duration / 1000).toFixed(2);
      const issueCount = moduleResult.result.issues.length;

      content += `### ${statusIcon} ${moduleResult.moduleName}

**Status:** ${this.getStatusBadge(moduleResult.status)}  
**Duration:** ${duration}s  
**Issues Found:** ${issueCount}

`;

      if (issueCount > 0) {
        const issues = moduleResult.result.issues.slice(0, maxIssues);

        content += `#### Issues\n\n`;

        for (let i = 0; i < issues.length; i++) {
          const issue = issues[i];
          const severityIcon = this.getSeverityIcon(issue.severity);

          content += `${i + 1}. **${severityIcon} ${issue.title}** (\`${issue.severity}\`)

   ${issue.description}

`;

          if (issue.recommendation) {
            content += `   >  **Recommendation:** ${issue.recommendation}

`;
          }

          if (options?.includeMetadata && issue.metadata) {
            content += `   <details>
   <summary>Technical Details</summary>

   \`\`\`json
   ${JSON.stringify(issue.metadata, null, 2)}
   \`\`\`
   </details>

`;
          }
        }

        if (moduleResult.result.issues.length > maxIssues) {
          const remaining = moduleResult.result.issues.length - maxIssues;
          content += `<details>
<summary>Show ${remaining} more issue${remaining === 1 ? '' : 's'}...</summary>

${moduleResult.result.issues
  .slice(maxIssues)
  .map((issue, index) => {
    const severityIcon = this.getSeverityIcon(issue.severity);
    return `${maxIssues + index + 1}. **${severityIcon} ${issue.title}** (\`${issue.severity}\`)

   ${issue.description}

   ${issue.recommendation ? `>  **Recommendation:** ${issue.recommendation}\n\n` : ''}`;
  })
  .join('')}
</details>

`;
        }
      } else {
        content += ` No issues detected in this module.

`;
      }

      // Module metadata
      if (options?.includeMetadata && moduleResult.result.metadata) {
        content += `<details>
<summary>Module Metadata</summary>

\`\`\`json
${JSON.stringify(moduleResult.result.metadata, null, 2)}
\`\`\`
</details>

`;
      }

      content += '---\n\n';
    }

    return content;
  }

  /**
   * Generate detailed issues section
   */
  private generateDetailedIssues(report: MonitoringReport, options?: ReportOptions): string {
    const allIssues = report.modules.flatMap(m =>
      m.result.issues.map(issue => ({ ...issue, module: m.moduleName }))
    );

    if (allIssues.length === 0) {
      return `## Detailed Issues

 **No issues were detected during the monitoring process.**`;
    }

    // Group by severity
    const severityGroups = {
      [SeverityLevel.CRITICAL]: allIssues.filter(i => i.severity === SeverityLevel.CRITICAL),
      [SeverityLevel.ERROR]: allIssues.filter(i => i.severity === SeverityLevel.ERROR),
      [SeverityLevel.WARNING]: allIssues.filter(i => i.severity === SeverityLevel.WARNING),
      [SeverityLevel.INFO]: allIssues.filter(i => i.severity === SeverityLevel.INFO),
    };

    let content = `## Detailed Issues

This section provides comprehensive details about all identified issues, organized by severity level.

`;

    for (const [severity, issues] of Object.entries(severityGroups)) {
      if (issues.length === 0) continue;

      const severityIcon = this.getSeverityIcon(severity as SeverityLevel);
      const severityName = severity.charAt(0).toUpperCase() + severity.slice(1).toLowerCase();

      content += `### ${severityIcon} ${severityName} Issues (${issues.length})

`;

      // Sort issues by module
      const sortedIssues = this.sortIssues(issues, options?.sortBy);

      for (let i = 0; i < sortedIssues.length; i++) {
        const issue = sortedIssues[i];

        content += `#### ${i + 1}. ${issue.title}

**Module:** ${issue.module}  
**Category:** \`${issue.category}\`  
**Severity:** ${this.getSeverityBadge(issue.severity)}

${issue.description}

`;

        if (issue.recommendation) {
          content += `> ** Recommendation**  
> ${issue.recommendation}

`;
        }

        if (options?.includeMetadata && issue.metadata) {
          content += `<details>
<summary>Technical Metadata</summary>

\`\`\`json
${JSON.stringify(issue.metadata, null, 2)}
\`\`\`
</details>

`;
        }

        content += '---\n\n';
      }
    }

    return content;
  }

  /**
   * Generate recommendations section
   */
  private generateRecommendations(report: MonitoringReport, options?: ReportOptions): string {
    if (!report.recommendations?.length) return '';

    let content = `## Recommendations

Based on the monitoring analysis, the following recommendations are suggested:

`;

    report.recommendations.forEach((recommendation, index) => {
      content += `${index + 1}. ${recommendation}\n\n`;
    });

    return content;
  }

  /**
   * Generate appendix with technical details
   */
  private generateAppendix(report: MonitoringReport, options?: ReportOptions): string {
    return `## Technical Details

### Execution Environment

| Property | Value |
|----------|-------|
| **Diamond Contract** | \`${report.diamond.address}\` |
| **Network** | ${report.network.name} (${report.network.chainId}) |
| **RPC URL** | \`${report.network.rpcUrl || 'N/A'}\` |
| **Block Explorer** | ${report.network.blockExplorerUrl || 'N/A'} |
| **Deployment Block** | ${report.diamond.deploymentBlock || 'N/A'} |
| **Config Path** | \`${report.diamond.configPath || 'N/A'}\` |

### Execution Statistics

| Metric | Value |
|--------|-------|
| **Total Execution Time** | ${(report.duration / 1000).toFixed(2)}s |
| **Modules Executed** | ${report.modules.length} |
| **Average Module Time** | ${(report.modules.reduce((sum, m) => sum + m.duration, 0) / report.modules.length / 1000).toFixed(2)}s |
| **Fastest Module** | ${this.getFastestModule(report)} |
| **Slowest Module** | ${this.getSlowestModule(report)} |

### Module Configuration

<details>
<summary>Show configuration details</summary>

\`\`\`json
${JSON.stringify(report.config, null, 2)}
\`\`\`
</details>

---

*Report generated by Diamond Monitoring System v1.0.0 on ${new Date().toISOString()}*`;
  }

  // Utility methods

  /**
   * Generate module performance table
   */
  private generateModulePerformanceTable(report: MonitoringReport): string {
    const rows = report.modules.map(m => {
      const statusIcon = this.getStatusIcon(m.status);
      const duration = (m.duration / 1000).toFixed(2);
      const issues = m.result.issues.length;

      return `| ${statusIcon} ${m.moduleName} | ${m.status} | ${duration}s | ${issues} |`;
    });

    return `| Module | Status | Duration | Issues |
|--------|--------|----------|--------|
${rows.join('\n')}`;
  }

  /**
   * Generate ASCII progress bar
   */
  private generateProgressBar(percentage: number, width: number = 20): string {
    const filled = Math.round((percentage / 100) * width);
    const empty = width - filled;
    const bar = ''.repeat(filled) + ''.repeat(empty);

    return `\`\`\`
Progress: [${bar}] ${percentage.toFixed(1)}%
\`\`\``;
  }

  /**
   * Get status badge
   */
  private getStatusBadge(status: MonitoringStatus): string {
    switch (status) {
      case MonitoringStatus.PASS:
        return '![PASS](https://img.shields.io/badge/Status-PASS-brightgreen)';
      case MonitoringStatus.WARNING:
        return '![WARNING](https://img.shields.io/badge/Status-WARNING-yellow)';
      case MonitoringStatus.FAIL:
        return '![FAIL](https://img.shields.io/badge/Status-FAIL-red)';
      case MonitoringStatus.SKIPPED:
        return '![SKIPPED](https://img.shields.io/badge/Status-SKIPPED-lightgrey)';
      default:
        return '![UNKNOWN](https://img.shields.io/badge/Status-UNKNOWN-lightgrey)';
    }
  }

  /**
   * Get severity badge
   */
  private getSeverityBadge(severity: SeverityLevel): string {
    switch (severity) {
      case SeverityLevel.CRITICAL:
        return '![CRITICAL](https://img.shields.io/badge/Severity-CRITICAL-red)';
      case SeverityLevel.ERROR:
        return '![ERROR](https://img.shields.io/badge/Severity-ERROR-orange)';
      case SeverityLevel.WARNING:
        return '![WARNING](https://img.shields.io/badge/Severity-WARNING-yellow)';
      case SeverityLevel.INFO:
        return '![INFO](https://img.shields.io/badge/Severity-INFO-blue)';
      default:
        return '![UNKNOWN](https://img.shields.io/badge/Severity-UNKNOWN-lightgrey)';
    }
  }

  /**
   * Get status icon
   */
  private getStatusIcon(status: MonitoringStatus): string {
    switch (status) {
      case MonitoringStatus.PASS:
        return '';
      case MonitoringStatus.WARNING:
        return '';
      case MonitoringStatus.FAIL:
        return '';
      case MonitoringStatus.SKIPPED:
        return '';
      default:
        return '';
    }
  }

  /**
   * Get severity icon
   */
  private getSeverityIcon(severity: SeverityLevel): string {
    switch (severity) {
      case SeverityLevel.CRITICAL:
        return '';
      case SeverityLevel.ERROR:
        return '';
      case SeverityLevel.WARNING:
        return '';
      case SeverityLevel.INFO:
        return '';
      default:
        return '';
    }
  }

  /**
   * Sort issues by specified criteria
   */
  private sortIssues(issues: any[], sortBy?: string): any[] {
    const sorted = [...issues];

    switch (sortBy) {
      case 'severity':
        const severityOrder: Record<string, number> = {
          [SeverityLevel.CRITICAL]: 0,
          [SeverityLevel.ERROR]: 1,
          [SeverityLevel.WARNING]: 2,
          [SeverityLevel.INFO]: 3,
        };
        sorted.sort(
          (a, b) => (severityOrder[a.severity] || 999) - (severityOrder[b.severity] || 999)
        );
        break;
      case 'category':
        sorted.sort((a, b) => a.category.localeCompare(b.category));
        break;
      case 'module':
        sorted.sort((a, b) => a.module.localeCompare(b.module));
        break;
    }

    return sorted;
  }

  /**
   * Get fastest module
   */
  private getFastestModule(report: MonitoringReport): string {
    const fastest = report.modules.reduce((min, m) => (m.duration < min.duration ? m : min));
    return `${fastest.moduleName} (${(fastest.duration / 1000).toFixed(2)}s)`;
  }

  /**
   * Get slowest module
   */
  private getSlowestModule(report: MonitoringReport): string {
    const slowest = report.modules.reduce((max, m) => (m.duration > max.duration ? m : max));
    return `${slowest.moduleName} (${(slowest.duration / 1000).toFixed(2)}s)`;
  }
}

/**
 * CSV Report Formatter
 *
 * Generates CSV reports suitable for data analysis, spreadsheet import,
 * and statistical processing with flattened data structure.
 */

import { MonitoringReport, MonitoringStatus, SeverityLevel } from '../../core/types';
import { ReportFormatter, ReportOptions, ReportValidationResult } from '../types';

/**
 * CSV formatter for data analysis
 */
export class CSVFormatter implements ReportFormatter {
  public readonly id = 'csv';
  public readonly name = 'CSV Report';
  public readonly extension = '.csv';
  public readonly mimeType = 'text/csv';

  /**
   * Format the monitoring report as CSV
   */
  public async format(report: MonitoringReport, options?: ReportOptions): Promise<string> {
    if (options?.compact) {
      return this.generateCompactCSV(report, options);
    } else {
      return this.generateDetailedCSV(report, options);
    }
  }

  /**
   * Validate options for CSV formatting
   */
  public validateOptions(options?: ReportOptions): ReportValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    // Warn about options that don't apply to CSV
    const irrelevantOptions = ['colorOutput', 'interactive', 'theme', 'includeCharts', 'customCss'];
    irrelevantOptions.forEach(option => {
      if (options && (options as any)[option]) {
        warnings.push(`${option} option is ignored for CSV format`);
      }
    });

    return {
      isValid: true,
      errors,
      warnings,
    };
  }

  // Private helper methods

  /**
   * Generate detailed CSV with all information
   */
  private generateDetailedCSV(report: MonitoringReport, options?: ReportOptions): string {
    const rows: string[][] = [];

    // Headers
    const headers = [
      'Report_Timestamp',
      'Diamond_Name',
      'Diamond_Address',
      'Network_Name',
      'Network_ChainId',
      'Overall_Status',
      'Total_Checks',
      'Passed',
      'Failed',
      'Warnings',
      'Skipped',
      'Total_Duration_Ms',
      'Module_Id',
      'Module_Name',
      'Module_Status',
      'Module_Start_Time',
      'Module_End_Time',
      'Module_Duration_Ms',
      'Issue_Count',
      'Issue_Id',
      'Issue_Title',
      'Issue_Description',
      'Issue_Severity',
      'Issue_Category',
      'Issue_Recommendation',
      'Issue_Metadata',
    ];

    if (options?.includeMetadata) {
      headers.push('Module_Metadata');
    }

    rows.push(headers);

    // Data rows
    for (const moduleResult of report.modules) {
      const baseRow = [
        report.timestamp.toISOString(),
        report.diamond.name,
        report.diamond.address,
        report.network.name,
        report.network.chainId.toString(),
        report.summary.status,
        report.summary.totalChecks.toString(),
        report.summary.passed.toString(),
        report.summary.failed.toString(),
        report.summary.warnings.toString(),
        report.summary.skipped.toString(),
        report.duration.toString(),
        moduleResult.moduleId,
        moduleResult.moduleName,
        moduleResult.status,
        moduleResult.startTime.toISOString(),
        moduleResult.endTime.toISOString(),
        moduleResult.duration.toString(),
        moduleResult.result.issues.length.toString(),
      ];

      if (moduleResult.result.issues.length === 0) {
        // No issues - add a row with empty issue fields
        const row = [...baseRow, '', '', '', '', '', '', ''];
        if (options?.includeMetadata) {
          row.push(this.stringifyMetadata(moduleResult.result.metadata));
        }
        rows.push(row);
      } else {
        // Add one row per issue
        for (const issue of moduleResult.result.issues) {
          const row = [
            ...baseRow,
            issue.id,
            issue.title,
            this.escapeCsvField(issue.description),
            issue.severity,
            issue.category,
            this.escapeCsvField(issue.recommendation || ''),
            this.stringifyMetadata(issue.metadata),
          ];

          if (options?.includeMetadata) {
            row.push(this.stringifyMetadata(moduleResult.result.metadata));
          }

          rows.push(row);
        }
      }
    }

    return this.formatCSVRows(rows);
  }

  /**
   * Generate compact CSV focused on issues
   */
  private generateCompactCSV(report: MonitoringReport, options?: ReportOptions): string {
    const sections: string[] = [];

    // Summary section
    sections.push('# SUMMARY');
    sections.push(this.generateSummaryCSV(report));

    // Module performance section
    sections.push('\n# MODULE_PERFORMANCE');
    sections.push(this.generateModulePerformanceCSV(report));

    // Issues section
    const allIssues = report.modules.flatMap(m =>
      m.result.issues.map(issue => ({ ...issue, module: m.moduleName }))
    );

    if (allIssues.length > 0) {
      sections.push('\n# ISSUES');
      sections.push(this.generateIssuesCSV(allIssues, options));
    }

    // Statistics section
    if (options?.includeMetadata) {
      sections.push('\n# STATISTICS');
      sections.push(this.generateStatisticsCSV(report));
    }

    return sections.join('\n');
  }

  /**
   * Generate summary CSV section
   */
  private generateSummaryCSV(report: MonitoringReport): string {
    const headers = ['Metric', 'Value'];
    const rows = [
      headers,
      ['Diamond_Name', report.diamond.name],
      ['Diamond_Address', report.diamond.address],
      ['Network', report.network.name],
      ['Chain_ID', report.network.chainId.toString()],
      ['Status', report.summary.status],
      ['Total_Checks', report.summary.totalChecks.toString()],
      ['Passed', report.summary.passed.toString()],
      ['Failed', report.summary.failed.toString()],
      ['Warnings', report.summary.warnings.toString()],
      ['Skipped', report.summary.skipped.toString()],
      ['Duration_Seconds', (report.duration / 1000).toFixed(2)],
      ['Timestamp', report.timestamp.toISOString()],
    ];

    return this.formatCSVRows(rows);
  }

  /**
   * Generate module performance CSV section
   */
  private generateModulePerformanceCSV(report: MonitoringReport): string {
    const headers = [
      'Module_Name',
      'Module_ID',
      'Status',
      'Duration_Ms',
      'Duration_Seconds',
      'Issue_Count',
      'Start_Time',
      'End_Time',
    ];
    const rows = [headers];

    for (const moduleResult of report.modules) {
      rows.push([
        moduleResult.moduleName,
        moduleResult.moduleId,
        moduleResult.status,
        moduleResult.duration.toString(),
        (moduleResult.duration / 1000).toFixed(2),
        moduleResult.result.issues.length.toString(),
        moduleResult.startTime.toISOString(),
        moduleResult.endTime.toISOString(),
      ]);
    }

    return this.formatCSVRows(rows);
  }

  /**
   * Generate issues CSV section
   */
  private generateIssuesCSV(issues: any[], options?: ReportOptions): string {
    const headers = [
      'Module',
      'Issue_ID',
      'Title',
      'Severity',
      'Category',
      'Description',
      'Recommendation',
      'Has_Metadata',
    ];

    if (options?.includeMetadata) {
      headers.push('Metadata');
    }

    const rows = [headers];

    // Sort issues if specified
    const sortedIssues = this.sortIssues(issues, options?.sortBy);

    // Apply filters
    let filteredIssues = sortedIssues;
    if (options?.severityFilter?.length) {
      filteredIssues = filteredIssues.filter(issue =>
        options.severityFilter!.includes(issue.severity)
      );
    }

    for (const issue of filteredIssues) {
      const row = [
        issue.module,
        issue.id,
        this.escapeCsvField(issue.title),
        issue.severity,
        issue.category,
        this.escapeCsvField(issue.description),
        this.escapeCsvField(issue.recommendation || ''),
        issue.metadata ? 'true' : 'false',
      ];

      if (options?.includeMetadata) {
        row.push(this.stringifyMetadata(issue.metadata));
      }

      rows.push(row);
    }

    return this.formatCSVRows(rows);
  }

  /**
   * Generate statistics CSV section
   */
  private generateStatisticsCSV(report: MonitoringReport): string {
    const allIssues = report.modules.flatMap(m => m.result.issues);

    // Severity distribution
    const severityStats = [
      ['Statistic', 'Value'],
      ['Total_Issues', allIssues.length.toString()],
      [
        'Critical_Issues',
        allIssues.filter(i => i.severity === SeverityLevel.CRITICAL).length.toString(),
      ],
      ['Error_Issues', allIssues.filter(i => i.severity === SeverityLevel.ERROR).length.toString()],
      [
        'Warning_Issues',
        allIssues.filter(i => i.severity === SeverityLevel.WARNING).length.toString(),
      ],
      ['Info_Issues', allIssues.filter(i => i.severity === SeverityLevel.INFO).length.toString()],
    ];

    // Category distribution
    const categoryMap = new Map<string, number>();
    allIssues.forEach(issue => {
      categoryMap.set(issue.category, (categoryMap.get(issue.category) || 0) + 1);
    });

    const categoryStats: string[][] = [
      ['', ''],
      ['# CATEGORY_DISTRIBUTION', ''],
      ['Category', 'Count'],
    ];

    for (const category of Array.from(categoryMap.keys())) {
      const count = categoryMap.get(category)!;
      categoryStats.push([category, count.toString()]);
    }

    // Module statistics
    const moduleStats: string[][] = [
      ['', ''],
      ['# MODULE_STATISTICS', ''],
      ['Module', 'Duration_Ms', 'Issue_Count', 'Issues_Per_Second'],
    ];

    for (const moduleResult of report.modules) {
      const issuesPerSecond =
        moduleResult.duration > 0
          ? (moduleResult.result.issues.length / (moduleResult.duration / 1000)).toFixed(3)
          : '0';

      moduleStats.push([
        moduleResult.moduleName,
        moduleResult.duration.toString(),
        moduleResult.result.issues.length.toString(),
        issuesPerSecond,
      ]);
    }

    return this.formatCSVRows([...severityStats, ...categoryStats, ...moduleStats]);
  }

  /**
   * Sort issues by specified criteria
   */
  private sortIssues(issues: any[], sortBy?: string): any[] {
    const sorted = [...issues];

    switch (sortBy) {
      case 'severity':
        const severityOrder: Record<string, number> = {
          [SeverityLevel.CRITICAL]: 0,
          [SeverityLevel.ERROR]: 1,
          [SeverityLevel.WARNING]: 2,
          [SeverityLevel.INFO]: 3,
        };
        sorted.sort(
          (a, b) => (severityOrder[a.severity] || 999) - (severityOrder[b.severity] || 999)
        );
        break;
      case 'category':
        sorted.sort((a, b) => a.category.localeCompare(b.category));
        break;
      case 'module':
        sorted.sort((a, b) => a.module.localeCompare(b.module));
        break;
      case 'timestamp':
        // Sort by issue ID as proxy for timestamp if no actual timestamp
        sorted.sort((a, b) => a.id.localeCompare(b.id));
        break;
    }

    return sorted;
  }

  /**
   * Format rows as CSV
   */
  private formatCSVRows(rows: string[][]): string {
    return rows.map(row => row.map(cell => this.escapeCSVCell(cell)).join(',')).join('\n');
  }

  /**
   * Escape CSV cell content
   */
  private escapeCSVCell(cell: string): string {
    // Handle null/undefined
    if (cell == null) return '';

    const str = String(cell);

    // If cell contains comma, newline, or double quote, wrap in quotes and escape quotes
    if (str.includes(',') || str.includes('\n') || str.includes('\r') || str.includes('"')) {
      return `"${str.replace(/"/g, '""')}"`;
    }

    return str;
  }

  /**
   * Escape CSV field content (for description and recommendation fields)
   */
  private escapeCsvField(text: string): string {
    if (!text) return '';

    // Replace newlines with spaces and limit length
    return text.replace(/\r?\n/g, ' ').replace(/\s+/g, ' ').trim().substring(0, 500); // Limit field length for CSV compatibility
  }

  /**
   * Convert metadata object to string
   */
  private stringifyMetadata(metadata: any): string {
    if (!metadata) return '';

    try {
      return JSON.stringify(metadata).replace(/"/g, '""');
    } catch {
      return String(metadata).replace(/"/g, '""');
    }
  }
}

/**
 * Enhanced list modules task for Hardhat
 *
 * Provides comprehensive module listing with filtering, detailed information, and professional output
 */

import { task } from 'hardhat/config';
import { HardhatRuntimeEnvironment } from 'hardhat/types';
import chalk from 'chalk';
import * as fs from 'fs';
import * as path from 'path';

import { DiamondMonitoringSystem } from '../core/DiamondMonitoringSystem';
import { MonitoringModule } from '../core/types';

/**
 * Extended task arguments for enhanced module listing
 */
interface EnhancedListModulesArgs {
  filter?: string;
  category?: string;
  debug?: boolean;
  format?: string;
  outputFile?: string;
  showDependencies?: boolean;
  showConfig?: boolean;
  sortBy?: string;
}

task(
  'list-modules',
  'List available diamond monitoring modules with enhanced filtering and information'
)
  .addOptionalParam('filter', 'Filter modules by name or description (regex supported)')
  .addOptionalParam('category', 'Filter modules by category')
  .addOptionalParam('format', 'Output format (console|json|table)', 'console')
  .addOptionalParam('outputFile', 'Save output to file')
  .addOptionalParam('sortBy', 'Sort modules by (name|category)', 'name')
  .addFlag('debug', 'Show detailed module information')
  .addFlag('showDependencies', 'Show module dependencies')
  .addFlag('showConfig', 'Show module configuration requirements')
  .setAction(async (taskArgs: EnhancedListModulesArgs, hre: HardhatRuntimeEnvironment) => {
    try {
      // Print header
      console.log(chalk.blue.bold('\n Available Diamond Monitoring Modules'));
      console.log(chalk.blue(''.repeat(45)));

      // Import and instantiate modules directly
      const {
        FunctionSelectorModule,
        DiamondStructureModule,
        AccessControlModule,
        TokenSupplyModule,
        ERC165ComplianceModule,
      } = await import('../modules');

      const moduleInstances: MonitoringModule[] = [
        new FunctionSelectorModule(),
        new DiamondStructureModule(),
        new AccessControlModule(),
        new TokenSupplyModule(),
        new ERC165ComplianceModule(),
      ];

      // Get all available modules
      const allModules = moduleInstances;

      if (allModules.length === 0) {
        console.log(chalk.yellow('  No monitoring modules found'));
        console.log(
          chalk.blue(' Make sure monitoring modules are properly installed and registered')
        );
        return;
      }

      // Apply filters
      let filteredModules = filterModules(allModules, taskArgs);

      // Sort modules
      filteredModules = sortModules(filteredModules, taskArgs.sortBy || 'name');

      // Display results based on format
      switch (taskArgs.format) {
        case 'json':
          await displayModulesJSON(filteredModules, taskArgs);
          break;
        case 'table':
          displayModulesTable(filteredModules, taskArgs);
          break;
        default:
          displayModulesConsole(filteredModules, taskArgs);
      }

      // Save to file if requested
      if (taskArgs.outputFile) {
        await saveModulesToFile(filteredModules, taskArgs);
      }

      // Display summary
      displaySummary(allModules, filteredModules, taskArgs);
    } catch (error) {
      console.error(chalk.red.bold('\n Failed to list modules:'));
      console.error(chalk.red(`   ${(error as Error).message}\n`));

      if (taskArgs.debug) {
        console.error(chalk.gray('Stack trace:'));
        console.error(chalk.gray((error as Error).stack));
      }

      throw error;
    }
  });

// ========================================
// Implementation Functions
// ========================================

/**
 * Filter modules based on criteria
 */
function filterModules(
  modules: MonitoringModule[],
  args: EnhancedListModulesArgs
): MonitoringModule[] {
  let filtered = [...modules];

  // Filter by name/description
  if (args.filter) {
    const regex = new RegExp(args.filter, 'i');
    filtered = filtered.filter(
      module => regex.test(module.name) || regex.test(module.description) || regex.test(module.id)
    );
  }

  // Filter by category
  if (args.category) {
    filtered = filtered.filter(
      module => module.category?.toLowerCase() === args.category!.toLowerCase()
    );
  }

  return filtered;
}

/**
 * Sort modules by specified criteria
 */
function sortModules(modules: MonitoringModule[], sortBy: string): MonitoringModule[] {
  return modules.sort((a, b) => {
    switch (sortBy) {
      case 'category':
        const categoryA = a.category || '';
        const categoryB = b.category || '';
        return categoryA.localeCompare(categoryB);

      case 'name':
      default:
        return a.name.localeCompare(b.name);
    }
  });
}

/**
 * Display modules in console format
 */
function displayModulesConsole(modules: MonitoringModule[], args: EnhancedListModulesArgs): void {
  if (modules.length === 0) {
    console.log(chalk.yellow(' No modules match the specified filters\n'));
    return;
  }

  console.log(chalk.blue(`\n Found ${modules.length} module(s):\n`));

  modules.forEach((module, index) => {
    // Module header
    console.log(chalk.green.bold(`${index + 1}. ${module.name}`));
    console.log(chalk.blue(`   ID: ${module.id}`));
    console.log(chalk.gray(`   ${module.description}`));

    // Category and version
    if (module.category) {
      console.log(chalk.cyan(`   Category: ${module.category}`));
    }
    if (module.version) {
      console.log(chalk.cyan(`   Version: ${module.version}`));
    }

    // Configuration requirements (if available)
    if (args.showConfig) {
      try {
        const configReqs = module.getRequiredConfig();
        if (configReqs && configReqs.length > 0) {
          console.log(chalk.magenta('   Configuration:'));
          configReqs.forEach(req => {
            const required = req.required ? chalk.red('*') : ' ';
            console.log(
              chalk.magenta(`     ${required} ${req.key} (${req.type}): ${req.description}`)
            );
          });
        }
      } catch (error) {
        console.log(chalk.gray('   Configuration: Not available'));
      }
    }

    console.log(); // Empty line between modules
  });
}

/**
 * Display modules in table format
 */
function displayModulesTable(modules: MonitoringModule[], args: EnhancedListModulesArgs): void {
  if (modules.length === 0) {
    console.log(chalk.yellow(' No modules match the specified filters\n'));
    return;
  }

  console.log('\n');

  // Header
  const nameWidth = Math.max(20, Math.max(...modules.map(m => m.name.length)) + 2);
  const categoryWidth = Math.max(12, Math.max(...modules.map(m => (m.category || '').length)) + 2);
  const idWidth = Math.max(15, Math.max(...modules.map(m => m.id.length)) + 2);

  const headerFormat = `${chalk.blue.bold('Name'.padEnd(nameWidth))}${chalk.blue.bold('Category'.padEnd(categoryWidth))}${chalk.blue.bold('ID'.padEnd(idWidth))}${chalk.blue.bold('Description')}`;
  console.log(headerFormat);
  console.log(''.repeat(nameWidth + categoryWidth + idWidth + 30));

  // Rows
  modules.forEach(module => {
    const name = module.name.padEnd(nameWidth);
    const category = (module.category || 'N/A').padEnd(categoryWidth);
    const id = module.id.padEnd(idWidth);
    const description =
      module.description.substring(0, 50) + (module.description.length > 50 ? '...' : '');

    console.log(
      `${chalk.green(name)}${chalk.cyan(category)}${chalk.gray(id)}${chalk.white(description)}`
    );
  });

  console.log();
}

/**
 * Display modules in JSON format
 */
async function displayModulesJSON(
  modules: MonitoringModule[],
  args: EnhancedListModulesArgs
): Promise<void> {
  const output = {
    timestamp: new Date().toISOString(),
    total: modules.length,
    filters: {
      filter: args.filter,
      category: args.category,
      sortBy: args.sortBy,
    },
    modules: modules.map(module => {
      const basicInfo = {
        id: module.id,
        name: module.name,
        description: module.description,
        category: module.category,
        version: module.version,
      };

      if (args.showConfig) {
        try {
          const configReqs = module.getRequiredConfig();
          return { ...basicInfo, configRequirements: configReqs };
        } catch (error) {
          return { ...basicInfo, configRequirements: null };
        }
      }

      return basicInfo;
    }),
  };

  console.log(JSON.stringify(output, null, 2));
}

/**
 * Save modules information to file
 */
async function saveModulesToFile(
  modules: MonitoringModule[],
  args: EnhancedListModulesArgs
): Promise<void> {
  if (!args.outputFile) return;

  try {
    const output = {
      timestamp: new Date().toISOString(),
      total: modules.length,
      filters: {
        filter: args.filter,
        category: args.category,
        sortBy: args.sortBy,
      },
      modules: modules.map(module => ({
        id: module.id,
        name: module.name,
        description: module.description,
        category: module.category,
        version: module.version,
      })),
    };

    const outputPath = path.resolve(args.outputFile);
    await fs.promises.writeFile(outputPath, JSON.stringify(output, null, 2), 'utf8');

    console.log(chalk.green(` Module list saved to: ${outputPath}`));
  } catch (error) {
    console.error(chalk.red(` Failed to save to file: ${(error as Error).message}`));
  }
}

/**
 * Display summary information
 */
function displaySummary(
  allModules: MonitoringModule[],
  filteredModules: MonitoringModule[],
  args: EnhancedListModulesArgs
): void {
  console.log(chalk.blue('\n Summary:'));
  console.log(chalk.blue(''.repeat(20)));

  console.log(chalk.blue(` Total modules available: ${allModules.length}`));

  if (args.filter || args.category) {
    console.log(chalk.blue(` Modules matching filters: ${filteredModules.length}`));
  }

  // Category breakdown
  const categories = allModules.reduce(
    (acc, module) => {
      const category = module.category || 'Uncategorized';
      acc[category] = (acc[category] || 0) + 1;
      return acc;
    },
    {} as Record<string, number>
  );

  if (Object.keys(categories).length > 1) {
    console.log(chalk.blue('\n By Category:'));
    Object.entries(categories)
      .sort(([, a], [, b]) => b - a)
      .forEach(([category, count]) => {
        console.log(chalk.cyan(`   ${category}: ${count}`));
      });
  }

  // Show available filters
  const allCategories = [...new Set(allModules.map(m => m.category).filter(Boolean))];
  if (allCategories.length > 0) {
    console.log(chalk.blue(`\n  Available categories: ${allCategories.join(', ')}`));
  }

  console.log();
}

/**
 * Continuous diamond monitoring task for Hardhat
 *
 * Provides dedicated continuous monitoring with enhanced scheduling, alerting, and reporting
 */

import { task } from 'hardhat/config';
import { HardhatRuntimeEnvironment } from 'hardhat/types';
import * as chalk from 'chalk';
import * as fs from 'fs';
import * as path from 'path';

import {
  DiamondInfo,
  MonitoringConfig,
  ReportFormat,
  MonitoringReport,
  MonitoringStatus,
} from '../core/types';
import { DiamondMonitoringSystem } from '../core/DiamondMonitoringSystem';
import { ReportGenerator } from '../reports/ReportGenerator';
import { ReportOptions } from '../reports/types';

/**
 * Extended task arguments for continuous monitoring
 */
interface ContinuousMonitoringArgs {
  diamondName: string;
  targetNetwork?: string;
  modules?: string;
  interval?: string;
  outputFormat?: string;
  outputDir?: string;
  configPath?: string;
  deploymentPath?: string;
  debug?: boolean;
  failOnError?: boolean;
  alertThreshold?: string;
  maxRuns?: string;
  logRotation?: boolean;
  autoRestart?: boolean;
}

task('monitor-continuous', 'Run continuous diamond monitoring with scheduling and alerting')
  .addPositionalParam('diamondName', 'Name of the diamond contract to monitor')
  .addOptionalParam('targetNetwork', 'Target network (overrides --network flag)')
  .addOptionalParam('modules', 'Comma-separated list of modules to run', 'all')
  .addOptionalParam('interval', 'Monitoring interval in seconds', '300')
  .addOptionalParam('outputFormat', 'Report format (console|json|html|markdown|csv)', 'console')
  .addOptionalParam('outputDir', 'Output directory for reports (auto-generated filenames)')
  .addOptionalParam('configPath', 'Path to custom configuration file')
  .addOptionalParam('deploymentPath', 'Path to diamond deployment files')
  .addOptionalParam('alertThreshold', 'Alert threshold (low|medium|high|critical)', 'high')
  .addOptionalParam('maxRuns', 'Maximum number of runs (0 = infinite)', '0')
  .addFlag('debug', 'Enable verbose logging and detailed output')
  .addFlag('failOnError', 'Exit with error code if monitoring fails')
  .addFlag('logRotation', 'Enable log rotation for output files')
  .addFlag('autoRestart', 'Automatically restart on critical failures')
  .setAction(async (taskArgs: ContinuousMonitoringArgs, hre: HardhatRuntimeEnvironment) => {
    const startTime = Date.now();

    try {
      // Print header
      console.log(chalk.blue.bold('\n  Continuous Diamond Monitoring System'));
      console.log(chalk.blue(''.repeat(45)));

      // Validate and setup monitoring
      await validateContinuousArgs(taskArgs, hre);

      const config = await loadContinuousConfiguration(taskArgs, hre);
      const diamond = await loadDiamondInfo(taskArgs, hre);

      // Initialize monitoring system
      const monitoringSystem = new DiamondMonitoringSystem();

      // Setup continuous monitoring
      await setupContinuousMonitoring(monitoringSystem, diamond, config, taskArgs, hre);
    } catch (error) {
      console.error(chalk.red.bold('\n Continuous monitoring setup failed:'));
      console.error(chalk.red(`   ${(error as Error).message}\n`));

      if (taskArgs.debug) {
        console.error(chalk.gray('Stack trace:'));
        console.error(chalk.gray((error as Error).stack));
      }

      process.exit(1);
    }
  });

// ========================================
// Implementation Functions
// ========================================

/**
 * Validate continuous monitoring arguments
 */
async function validateContinuousArgs(
  taskArgs: ContinuousMonitoringArgs,
  hre: HardhatRuntimeEnvironment
): Promise<void> {
  if (taskArgs.debug) {
    console.log(chalk.blue(' Validating continuous monitoring arguments...'));
  }

  // Validate interval
  const interval = parseInt(taskArgs.interval || '300');
  if (isNaN(interval) || interval < 30) {
    throw new Error('Monitoring interval must be at least 30 seconds');
  }

  // Validate max runs
  if (taskArgs.maxRuns) {
    const maxRuns = parseInt(taskArgs.maxRuns);
    if (isNaN(maxRuns) || maxRuns < 0) {
      throw new Error('Max runs must be a non-negative number');
    }
  }

  // Validate alert threshold
  const validThresholds = ['low', 'medium', 'high', 'critical'];
  if (taskArgs.alertThreshold && !validThresholds.includes(taskArgs.alertThreshold)) {
    throw new Error(
      `Invalid alert threshold '${taskArgs.alertThreshold}'. Valid options: ${validThresholds.join(', ')}`
    );
  }

  // Validate output directory
  if (taskArgs.outputDir) {
    if (!fs.existsSync(taskArgs.outputDir)) {
      fs.mkdirSync(taskArgs.outputDir, { recursive: true });
      console.log(chalk.green(` Created output directory: ${taskArgs.outputDir}`));
    }
  }

  if (taskArgs.debug) {
    console.log(chalk.green(' Arguments validated successfully'));
  }
}

/**
 * Load continuous monitoring configuration
 */
async function loadContinuousConfiguration(
  taskArgs: ContinuousMonitoringArgs,
  hre: HardhatRuntimeEnvironment
): Promise<MonitoringConfig> {
  if (taskArgs.debug) {
    console.log(chalk.blue('  Loading continuous monitoring configuration...'));
  }

  let config: Partial<MonitoringConfig> = {};

  // Load from configuration file if provided
  if (taskArgs.configPath) {
    try {
      const configContent = fs.readFileSync(taskArgs.configPath, 'utf8');
      config = JSON.parse(configContent);
    } catch (error) {
      throw new Error(
        `Failed to load configuration from ${taskArgs.configPath}: ${(error as Error).message}`
      );
    }
  }

  // Parse modules list
  const modulesList =
    taskArgs.modules !== 'all'
      ? taskArgs.modules
          ?.split(',')
          .map(m => m.trim())
          .filter(m => m) || []
      : [];

  // Create monitoring configuration
  const monitoringConfig: MonitoringConfig = {
    modules: config.modules || {},
    execution: {
      parallelExecution: config.execution?.parallelExecution || false,
      maxConcurrency: config.execution?.maxConcurrency || 3,
      timeoutMs: config.execution?.timeoutMs || 60000, // Increased for continuous
      failFast: config.execution?.failFast || false,
    },
    reporting: {
      format: (taskArgs.outputFormat as ReportFormat) || 'console',
      outputPath: taskArgs.outputDir,
      verbose: taskArgs.debug || false,
      includeMetadata: config.reporting?.includeMetadata || true,
    },
    network: {
      name: taskArgs.targetNetwork || hre.network.name,
      chainId: 0, // Will be set when loading diamond info
      rpcUrl: '', // Will be set when loading diamond info
    },
    diamond: {} as DiamondInfo, // Will be set when loading diamond info
  };

  // Configure specific modules if provided
  if (modulesList.length > 0) {
    modulesList.forEach(moduleId => {
      monitoringConfig.modules[moduleId] = {
        enabled: true,
        config: config.modules?.[moduleId]?.config || {},
      };
    });
  }

  if (taskArgs.debug) {
    console.log(chalk.green(' Configuration loaded for continuous monitoring'));
  }

  return monitoringConfig;
}

/**
 * Load diamond information for continuous monitoring
 */
async function loadDiamondInfo(
  taskArgs: ContinuousMonitoringArgs,
  hre: HardhatRuntimeEnvironment
): Promise<DiamondInfo> {
  if (taskArgs.debug) {
    console.log(chalk.blue(' Loading diamond information...'));
  }

  const networkName = taskArgs.targetNetwork || hre.network.name;
  const deploymentPath =
    taskArgs.deploymentPath || path.join(hre.config.paths.root, 'deployments', networkName);

  // Try to find diamond deployment file
  const possiblePaths = [
    path.join(deploymentPath, `${taskArgs.diamondName}.json`),
    path.join(deploymentPath, `${taskArgs.diamondName}Diamond.json`),
    path.join(deploymentPath, 'Diamond.json'),
    path.join(
      hre.config.paths.root,
      'diamond-deployments',
      networkName,
      `${taskArgs.diamondName}.json`
    ),
  ];

  let diamondDeployment: any = null;
  let deploymentFilePath: string = '';

  for (const filePath of possiblePaths) {
    if (fs.existsSync(filePath)) {
      try {
        diamondDeployment = JSON.parse(fs.readFileSync(filePath, 'utf8'));
        deploymentFilePath = filePath;
        break;
      } catch (error) {
        if (taskArgs.debug) {
          console.log(chalk.yellow(`  Failed to parse ${filePath}: ${(error as Error).message}`));
        }
      }
    }
  }

  if (!diamondDeployment) {
    throw new Error(
      `Diamond deployment not found for '${taskArgs.diamondName}' on network '${networkName}'`
    );
  }

  // Get network configuration
  const networkConfig = hre.config.networks[networkName];
  let rpcUrl = '';
  let chainId = 0;

  if (networkConfig && 'url' in networkConfig) {
    rpcUrl = networkConfig.url || '';
  }

  try {
    const provider = (hre as any).ethers.provider;
    const network = await provider.getNetwork();
    chainId = Number(network.chainId);
  } catch (error) {
    if (taskArgs.debug) {
      console.log(chalk.yellow(`  Could not get chain ID: ${(error as Error).message}`));
    }
  }

  const diamond: DiamondInfo = {
    name: taskArgs.diamondName,
    address: diamondDeployment.address || diamondDeployment.diamond?.address,
    configPath: deploymentFilePath,
    deploymentBlock: diamondDeployment.receipt?.blockNumber,
    network: {
      name: networkName,
      chainId,
      rpcUrl,
    },
  };

  if (!diamond.address) {
    throw new Error(`Diamond address not found in deployment file: ${deploymentFilePath}`);
  }

  if (taskArgs.debug) {
    console.log(chalk.green(` Diamond loaded: ${diamond.name} at ${diamond.address}`));
  }

  return diamond;
}

/**
 * Setup and run continuous monitoring
 */
async function setupContinuousMonitoring(
  system: DiamondMonitoringSystem,
  diamond: DiamondInfo,
  config: MonitoringConfig,
  taskArgs: ContinuousMonitoringArgs,
  hre: HardhatRuntimeEnvironment
): Promise<void> {
  const interval = parseInt(taskArgs.interval || '300') * 1000;
  const maxRuns = parseInt(taskArgs.maxRuns || '0');

  console.log(chalk.yellow.bold('\n  STARTING CONTINUOUS MONITORING'));
  console.log(chalk.yellow(`   Diamond: ${diamond.name} (${diamond.address})`));
  console.log(
    chalk.yellow(`   Network: ${diamond.network.name} (Chain ID: ${diamond.network.chainId})`)
  );
  console.log(chalk.yellow(`   Interval: ${interval / 1000} seconds`));
  console.log(chalk.yellow(`   Max Runs: ${maxRuns === 0 ? 'unlimited' : maxRuns}`));
  console.log(chalk.yellow(`   Alert Threshold: ${taskArgs.alertThreshold}`));
  console.log(chalk.yellow('   Press Ctrl+C to stop'));
  console.log(chalk.yellow(''.repeat(60)));

  let runCount = 0;
  let consecutiveFailures = 0;
  const maxConsecutiveFailures = 3;

  // Setup signal handlers for graceful shutdown
  let isShuttingDown = false;
  process.on('SIGINT', () => {
    if (!isShuttingDown) {
      isShuttingDown = true;
      console.log(chalk.yellow('\n\n Graceful shutdown initiated...'));
      console.log(chalk.blue(` Total monitoring runs completed: ${runCount}`));
      console.log(chalk.blue(' Saving final state...'));
      process.exit(0);
    }
  });

  process.on('SIGTERM', () => {
    if (!isShuttingDown) {
      isShuttingDown = true;
      console.log(chalk.yellow('\n\n Graceful shutdown initiated...'));
      process.exit(0);
    }
  });

  // Main monitoring loop
  const runMonitoring = async (): Promise<void> => {
    if (isShuttingDown) return;

    runCount++;
    const timestamp = new Date().toISOString();

    console.log(chalk.blue(`\n[${timestamp}] Monitoring run #${runCount}`));
    console.log(chalk.blue(''.repeat(60)));

    try {
      const provider = (hre as any).ethers.provider;
      const moduleIds =
        Object.keys(config.modules).length > 0 ? Object.keys(config.modules) : undefined;

      // Execute monitoring
      const report = await system.runMonitoring(diamond, provider, config, moduleIds);

      // Handle results
      await handleMonitoringResults(report, taskArgs, runCount);

      // Check for alerts
      await checkAlerts(report, taskArgs);

      // Reset consecutive failures on success
      consecutiveFailures = 0;

      console.log(chalk.green(` Run #${runCount} completed successfully`));
    } catch (error) {
      consecutiveFailures++;

      console.error(chalk.red(` Run #${runCount} failed: ${(error as Error).message}`));

      if (taskArgs.debug) {
        console.error(chalk.gray((error as Error).stack));
      }

      // Check if we should restart or exit
      if (consecutiveFailures >= maxConsecutiveFailures) {
        if (taskArgs.autoRestart) {
          console.log(
            chalk.yellow(
              ` Auto-restarting after ${maxConsecutiveFailures} consecutive failures...`
            )
          );
          consecutiveFailures = 0;
        } else {
          console.error(
            chalk.red.bold(` Stopping after ${maxConsecutiveFailures} consecutive failures`)
          );
          process.exit(1);
        }
      }
    }
  };

  // Run initial monitoring
  await runMonitoring();

  // Check if we should exit after first run
  if (maxRuns === 1) {
    console.log(chalk.blue('\n Single run completed'));
    return;
  }

  // Setup interval for continuous monitoring
  const intervalId = setInterval(async () => {
    if (isShuttingDown) {
      clearInterval(intervalId);
      return;
    }

    if (maxRuns > 0 && runCount >= maxRuns) {
      clearInterval(intervalId);
      console.log(chalk.blue(`\n Completed maximum ${maxRuns} runs`));
      process.exit(0);
      return;
    }

    await runMonitoring();
  }, interval);

  // Keep process alive
  return new Promise(() => {});
}

/**
 * Handle monitoring results
 */
async function handleMonitoringResults(
  report: MonitoringReport,
  taskArgs: ContinuousMonitoringArgs,
  runCount: number
): Promise<void> {
  // Generate timestamped filename if output directory is specified
  if (taskArgs.outputDir && taskArgs.outputFormat !== 'console') {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const filename = `monitoring-${taskArgs.diamondName}-${timestamp}-run${runCount}.${getFileExtension(taskArgs.outputFormat)}`;
    const outputPath = path.join(taskArgs.outputDir, filename);

    try {
      const format = taskArgs.outputFormat as ReportFormat;
      const options: ReportOptions = {
        includeMetadata: true,
        includeDetails: taskArgs.debug,
        colorOutput: false, // Disable colors for file output
        includeRecommendations: true,
      };

      await ReportGenerator.generateReport(report, format, outputPath, options);

      if (taskArgs.debug) {
        console.log(chalk.green(` Report saved: ${outputPath}`));
      }
    } catch (error) {
      console.error(chalk.red(` Failed to save report: ${(error as Error).message}`));
    }
  }

  // Display summary to console
  displayRunSummary(report, runCount, taskArgs.debug);
}

/**
 * Check for alerts based on threshold
 */
async function checkAlerts(
  report: MonitoringReport,
  taskArgs: ContinuousMonitoringArgs
): Promise<void> {
  const threshold = taskArgs.alertThreshold || 'high';
  const criticalIssues = report.modules.flatMap(m =>
    m.result.issues.filter(issue => shouldAlert(issue.severity, threshold))
  );

  if (criticalIssues.length > 0) {
    console.log(chalk.red.bold(`\n ALERT: ${criticalIssues.length} critical issue(s) detected!`));
    criticalIssues.forEach(issue => {
      console.log(chalk.red(`    ${issue.title} (${issue.severity})`));
    });

    // Here you could integrate with alerting systems (email, Slack, PagerDuty, etc.)
    // await sendAlert(criticalIssues, taskArgs);
  }
}

/**
 * Check if issue severity should trigger alert
 */
function shouldAlert(severity: string, threshold: string): boolean {
  const severityLevels = { info: 0, warning: 1, error: 2, critical: 3 };
  const thresholdLevels = { low: 0, medium: 1, high: 2, critical: 3 };

  const severityLevel = severityLevels[severity.toLowerCase() as keyof typeof severityLevels] ?? 0;
  const thresholdLevel =
    thresholdLevels[threshold.toLowerCase() as keyof typeof thresholdLevels] ?? 2;

  return severityLevel >= thresholdLevel;
}

/**
 * Display run summary
 */
function displayRunSummary(report: MonitoringReport, runCount: number, verbose?: boolean): void {
  const { summary } = report;

  // Status indicator
  let statusIcon = '';
  let statusColor = chalk.green;

  if (summary.failed > 0) {
    statusIcon = '';
    statusColor = chalk.red;
  } else if (summary.warnings > 0) {
    statusIcon = '';
    statusColor = chalk.yellow;
  }

  console.log(
    statusColor(`${statusIcon} Run #${runCount}: ${summary.status} (${report.duration}ms)`)
  );
  console.log(
    chalk.blue(
      `   Checks: ${summary.totalChecks} | Passed: ${summary.passed} | Failed: ${summary.failed} | Warnings: ${summary.warnings}`
    )
  );

  // Show module details in verbose mode
  if (verbose && report.modules.length > 0) {
    report.modules.forEach(module => {
      if (module.result.issues.length > 0) {
        console.log(chalk.blue(`   ${module.moduleName}: ${module.result.issues.length} issue(s)`));
      }
    });
  }
}

/**
 * Get file extension for report format
 */
function getFileExtension(format?: string): string {
  switch (format) {
    case 'json':
      return 'json';
    case 'html':
      return 'html';
    case 'markdown':
      return 'md';
    case 'csv':
      return 'csv';
    default:
      return 'txt';
  }
}

